updateData.functionBody:
let onSubmit = (formValue,formObject) => {
    admin.getCommandMessenger().compoundDataCommand([["formValue",formValue],["formMeta",formObject.getMeta()]])
}

return [
        {
            type: "radioButtonGroup",
            label: "Input Type: ",
            entries: [["Form","form"],["JSON","json"],["Raw ChartJS Config","rawConfig"]],
            horizontal: true,
            value: "form", //initial default
            key: "inputType",
        },    
        {
            type: "panel",
            selector: {
                parentKey: "inputType",
                parentValue: "json"
            },
            formData: [
                {
                    type: "textField",
                    label: "JSON Config: ",
                    key: "jsonConfigFile",
                    meta: {
                        "expression": "simple"
                    }
                }
            ],
            key: "jsonPanel",
            meta: {
                "expression": "object"
            }
        },
        {
            type: "panel",
            selector: {
                parentKey: "inputType",
                parentValue: "rawConfig"
            },
            formData: [ 
                {
                    type: "textField",
                    label: "ChartJS Config: ",
                    key: "configFile",
                    meta: {
                        "expression": "simple"
                    }
                }
            ],
            key: "configPanel",
            meta: {
                "expression": "object"
            }
        },
        {
            type: "panel",
            selector: {
                parentKey: "inputType",
                parentValue: "form"
            },
            formData: [
                {   
                    type: "dropdown",
                    label: "Chart Type: ",
                    entries: [["Bar","bar"],["Line","line"]],
                    value: "line", //initial default
                    key: "chartType"
                },  
                {   
                    type: "radioButtonGroup",
                    label: "Specifying X Values: ",
                    entries: [["Common Values","common"],["Per Series Values","paired"]],
                    value: "common", //initial default
                    horizontal: true,
                    key: "xValuesType"
                },
                {   
                    type: "textField",
                    label: "Common X Values: ",
                    key: "xValues",
                    selector: {
                        parentKey: "xValuesType",
                        parentValue: "common"
                    },
                    meta: {
                        "expression": "simple"
                    }
                },
                {   
                    type: "textField",
                    label: "Plot Series Data: ",
                    key: "dataSeries",
                    meta: {
                        "expression": "simple"
                    }
                },
                {   
                    type: "textField",
                    label: "Chart Options: ",
                    key: "chartOptions",
                    meta: {
                        "expression": "simple"
                    }
                }
            ],
            key: "formPanel",
            meta: {
                "expression": "object"
            }
        },
        {
            type: "submit",
            onSubmit: onSubmit
        }
    ];
