    updatedRecords.forEach( record => {
        var base = combinedEntries[record.Id];
        if(!base) throw new Error("No initial record for update record id = " + record.Id);
        base.newEntry = record;
    })

    for(var id in combinedEntries) {
        var combinedEntry = combinedEntries[id];
        if(combinedEntry.newEntry === undefined) throw new Error("No target record for update record id = " + id);
    }
}

