    createdRecords.forEach( record => {
        if(combinedEntries[record.Id] !== undefined) throw new Error("Repeart entry for id = " + record.Id);
        combinedEntries[record.Id] = {"newEntry":record};
    });
}

if(deletedRecords) {
    deletedRecords.forEach( record => {
        if(combinedEntries[record.Id] !== undefined) throw new Error("Repeart entry for id = " + record.Id);
        combinedEntries[record.Id] = {"oldEntry":record};
    });
}

