    featureTypePropData.legacyValue = "space";

    return newFileEntry;
}

function updateGeometryEntry(fileEntry) {
    var newFileEntry = apogeeutil.jsonCopy(fileEntry);
    var featureTypePropData = newFileEntry.Fields.entry_data.properties[0];
    if(featureTypePropData.key != "featureType") throw new Error("Not the feature type!");

    var legacyKey = featureTypePropData.legacyKey;
    var legacyValue = featureTypePropData.value;

    if(legacyValue == legacyKey) {
        legacyValue = "true";
    }

    featureTypePropData.legacyValue = legacyValue;

    return newFileEntry;
}

