class GeneratorClass {
    constructor() {
        //KLUDGE
        if(!window.mmGlobals) {
            this._initGlobals();
        }
    }

    onLoad(outputElement,admin) {
        if(window.mmGlobals) {
            if(!window.mmGlobals.msgHandlers) {
                //start listener and send "started" msg 
                //if this is the first time to set the handlers.
                window.addEventListener("message",window.mmGlobals.receiveMessage);
                console.log("Message listener started!");
                window.mmGlobals.sendMessage("opened",{});
                console.log("Started message sent!!");
            }
            window.mmGlobals.msgHandlers = {
                initModules: data => admin.getMessenger().dataUpdate("initModuleData",data),
                appStatus: data => admin.getMessenger().dataUpdate("appStatusData",data),
            };
        }
    }

    /** Alternatively we could have called model functions we loaded here. */
    //setData(msgHandlers,outputElement,admin) {
    //
    //}

    //===============================
    // Private Methods
    //===============================
    _initGlobals() {
        let mmGlobals = {};

        //get the calling window
        mmGlobals.callingWindow = window.opener;

        //lod the url params
        var params = new URLSearchParams(window.location.search);

        mmGlobals.callingUrl = params.get("callingUrl");
        if(!mmGlobals.callingUrl) {
            this._fatalError("CallingUrl not found in input.");
            return false;
        }

        mmGlobals.windowId = params.get("windowId");
        if(!mmGlobals.windowId) {
            this._fatalError("Window ID not found in input.");
            return false;
        }

        mmGlobals.sendMessage = function(messageType,commandData) {
            let messageData = {};
            messageData.commandData = commandData;

            //this identifies the window sending the message
            messageData.windowId = mmGlobals.windowId;

            if((mmGlobals.callingWindow)&&(mmGlobals.callingWindow.postMessage)) {
                console.log("sending message: " + messageType);
                mmGlobals.callingWindow.postMessage({message: messageType, value: messageData},mmGlobals.callingUrl);
            }
        }   

        mmGlobals.receiveMessage = function(event) {
            console.log("received message: " + event.data.message);
            switch(event.data.message) {
                case "initModules": 
                    mmGlobals.msgHandlers.initModules(event.data.value);
                    break;

                case "appStatus": 
                    mmGlobals.msgHandlers.appStatus(event.data.value);
                    break;

                case "appClosed": 
                    window.close();
                    break;
            }
        }

        window.mmGlobals = mmGlobals;
    }

    /** Temporary fatal error handling */
    _fatalError(msg) {
        throw new Error(msg);
    }
}

return new GeneratorClass();

