let container;       
let network;

__customControlDebugHook();

return {
    onLoad: function(outputElement,admin) {
        container = document.getElementById("visjs_container");
    },

    setData: function(data) {
        if(network) {
            network.destroy();
            network = null;
        }
        let dataCopy = apogeeutil.jsonCopy(data);
        let visData = {
            nodes: new vis.DataSet(dataCopy.nodes),
            edges: new vis.DataSet(dataCopy.edges)
        }
        network = new vis.Network(container,visData,data.options);
    },

    destroy: function() {
        if(network) {
            network.destroy();
            network = null;
        }
        container = nulll;
    }
}
