var Generator = class {
    onLoad(outputElement,admin) {
        this.textField = document.getElementById("cdc-xxx");
        this.textField.oninput = () => admin.startEditMode();

        this.span = document.getElementById("cdc-yyy");
    }

    setData(data,outputElement,admin) {
        this.textField.value = data.data;
        this.span.innerHTML = data.input;
    }

    getData(outputElement,admin) {
        return this.textField.value;
    }
}

return new Generator();

var Generator = class {

    constructor() {
        __customControlDebugHook();
    }

    onLoad(outputElement,admin) {
        this.textField = document.getElementById("cc-xxx");

        this.button = document.getElementById("cc-yyy");
        this.button.onclick = () => this._submit(admin);
    }

    setData(data,outputElement,admin) {
        this.textField.value = data;
    }


    //====================
    // Private
    //====================

    _submit(admin) {
        var command  = admin.getMessenger().getDataUpdateCommand("ccOutput",this.textField.value);
        admin.getMessenger().executeCommand(command);

    }
}

return new Generator();
