var tagMap = {};

var data = response.elements;
for(var i = 0; i < data.length; i++) {
    var feature = data[i];
    var properties = feature.tags;

    for(var key in properties) {
        var keyEntry = tagMap[key];
        if(!keyEntry) {
            keyEntry = {};
            tagMap[key] = keyEntry;
        }

        var value = properties[key];
        if(keyEntry[value] === undefined) {
            keyEntry[value] = 1;
        }
        else {
            keyEntry[value]++;
        }
    }
}

return tagMap;

var output = [];

for(var key in tags) {
    var entry = tags[key];
    var newEntry = apogee.util.deepJsonCopy(entry);
    newEntry.score = getScore(newEntry);
    newEntry.key = key;
    output.push(newEntry);
}


var compare = function(a,b) {
    return b.score - a.score;
}

output.sort(compare);

return output;

