let defaultConverter = utilities.generatorToLayout;

//panel converter
let panelConverter = generatorEntry => {
    let formEntry = {};
    formEntry.type = "panel";
    formEntry.formData = generatorEntry.value.formData.map(convertToLayout);
    formEntry.key = generatorEntry.value.key;
    return formEntry;
}

let listConverter = generatorEntry => {
    let formEntry = {};
    formEntry.type = "list";
    if(generatorEntry.value.label) formEntry.label = generatorEntry.value.label;
    formEntry.entryTypes = generatorEntry.value.entryTypes.map( inTypeEntry => {
        let buttonLabel = inTypeEntry.value.addButton;
        let effectiveTypeEntry = {};
        effectiveTypeEntry.key = inTypeEntry.key;
        effectiveTypeEntry.value = inTypeEntry.value[inTypeEntry.key];

        let formData = convertToLayout(effectiveTypeEntry);

        let outTypeEntry = {};
        outTypeEntry.label = buttonLabel;
        outTypeEntry.layout = formData;

        return outTypeEntry;
    });
    formEntry.key = generatorEntry.value.key;
    return formEntry;
}

let converterMap = {};

converterMap.panel = panelConverter;
converterMap.list = listConverter;

