let json = apogeeutil.jsonCopy(functionBodyEval);

let remap = (source,target,mapping) => {
    for(let fromKey in mapping) {
        let toKey = mapping[fromKey];
        if(source[fromKey] !== undefined) {
            target[toKey] = source[fromKey];
        }
    }
}

if(json.dataSeries) {
    json.dataSeries.forEach( entry => {
        let seriesOptions = entry.seriesOptions;
        if(seriesOptions.point) {
            let pointOptions = seriesOptions.point;
            delete seriesOptions.point;

            let pointMap = {
                pointStyle: "pointStyle",
                radius: "pointRadius",
                borderColor: "pointBorderColor",
                borderWidth: "pointBorderWidth",
                backgroundColor: "pointBackgroundColor"
            }

            remap(pointOptions,seriesOptions,pointMap);

        }
        if(seriesOptions.line) {
            let lineOptions = seriesOptions.line;
            delete seriesOptions.line;

            //this is a straight mapping, we could use assign
            let lineMap = {
                borderColor: "borderColor",
                borderWidth: "borderWidth",
                fill: "fill",
                backgroundColor: "backgroundColor"
            }

            remap(lineOptions,seriesOptions,lineMap);
        }
