function getOptions() {

    var options = {};

    //filter geometry with styles
    options.filter = theme.mapFilter

    //style lines and polygons
    options.style = theme.getLineAndAreaStyle;

    //style points
    options.pointToLayer = function(feature,latLng) {
        var iconUrl = theme.getPointIconUrl(feature);
        if(iconUrl) {
            var options = {"icon":L.icon({"iconUrl": iconUrl})};
            return L.marker(latLng, options);
        }
        else {
            return null;
        }
    };

    //add a popup
    options.onEachFeature = function(feature,layer) {
        var outputLines = [];
        if(feature.properties.name) outputLines.push(feature.properties.name);
        if(feature.properties.amenity) outputLines.push(feature.properties.amenity);
        if(feature.properties.cuisine) outputLines.push(feature.properties.cuisine);

        if(outputLines.length > 0) {
            layer.bindPopup(outputLines.join("<br>"));
        }
    }

    return options;   
}

function getLeafletBounds(swneBoundingBox) {
    var bottomRight = L.latLng(swneBoundingBox[0], swneBoundingBox[1]);
    var topLeft = L.latLng(swneBoundingBox[2], swneBoundingBox[3]);
    return L.latLngBounds(bottomRight, topLeft);
}

