var output = {};

var isNode = (element) => (element.type == "node");
var addToMap = (element) => output[element.id] = [element.lon,element.lat];

osmData.elements.filter(isNode).forEach(addToMap);

return output;

var output = {};
output.type = "FeatureCollection";
output.properties = {};

var hasTags = (element) => (element.tags !== undefined);

var toGeojsonFeature = (osmFeature) => {
    var geojsonFeature = {};
    geojsonFeature.type = "Feature";
    geojsonFeature.id = osmFeature.id;
    geojsonFeature.properties = osmFeature.tags;
    geojsonFeature.geometry = getGeojsonGeometry(osmFeature);
    return geojsonFeature;
}

output.features = osmData.elements.filter(hasTags).map(toGeojsonFeature);

return output;

updateData.supplementalCode:
function getGeojsonGeometry(osmFeature) {
    var geometry = {};
    if(osmFeature.type == "node") {
        geometry.type = "Point";
        geometry.coordinates = getPointCoordinates(osmFeature.id);
    }
    else if(osmFeature.type == "way") {
        if(propertyAnalysis.isPolygon(osmFeature)) {
            geometry.type = "Polygon";
            geometry.coordinates = getPolygonCoordinates(osmFeature.nodes)
        }
        else {
            geometry.type = "LineString";
            geometry.coordinates = getLineStringCoordinates(osmFeature.nodes);
        }
    }
    else {
        throw new Error("Unexpected osm type: " + osmFeature.type);
    }
    return geometry;
}

// function isPolygon(osmFeature) {
//     return false;
// }

/** Point - lookup lon lat point for given node id */
function getPointCoordinates(nodeId) {
    return geojsonPointLookupTable[nodeId];
}

/** Line String - list of lat lon points for a list of node ids */
function getLineStringCoordinates(nodeIdList) {
    return nodeIdList.map(getPointCoordinates);
}

/** Polygon - get a list o fline strings (with one entry - the outer loop)
 * We are not supporting interior rings for now. */
function getPolygonCoordinates(nodeIdList) {
    return [getLineStringCoordinates(nodeIdList)];
}

