function createWayStyleInfo(row) {
    var fillColor = getRowEntry(row,"Fill Color");
    var strokeColor = getRowEntry(row,"Stroke Color");
    var strokeWeight = getRowEntry(row,"Stroke Weight");

    var styleInfo = {};
    var entryAdded = false;

    if(fillColor != "") {
        styleInfo.fillColor = fillColor;
        entryAdded = true;
    }
    if(strokeColor != "") {
        styleInfo.color = strokeColor;
        if(strokeWeight != "") {
            styleInfo.weight = strokeWeight;
        }
        entryAdded = true;
    }

    if(entryAdded) {
        return styleInfo;
    }
    else {
        return null;
    }
}

function createPointStyleInfo(row) {
    var url = getRowEntry(row,"Icon Url");

    if(url != "") {
        style = {};
        style.iconUrl = url;
        var anchorString = getRowEntry(row,"Icon Anchor");
        if(anchorString) {
            style.iconAnchor = JSON.parse(anchorString);
        }
        return style;
    }
    else {
        return null;
    }
}

function polygonDetector(row) {
    var isPolygon = getRowEntry(row,"Is Polygon");

    if(isPolygon) {
        return true;
    }
    else {
        return null;
    }
}

