var propertyTree = {};

//don't include header row
for(var i = 1; i < inputMatrix.length; i++) {
    var row = inputMatrix[i];

    var payload = createPayloadFunction(row);
    if(payload) {
        var key = getRowEntry(row,"Key");
        var value = getRowEntry(row,"Value");

        //get key entry
        var keyEntry = propertyTree[key];
        if(keyEntry === undefined) {
            var keyEntry = {};
            propertyTree[key] = keyEntry;
        }

        //add style to key or value 
        if(value != "") {
            if(keyEntry.values === undefined) {
                keyEntry.values = {};
            }
            keyEntry.values[value] = payload; 
        }
        else {
            keyEntry.default = payload;
        }
    }
}

return propertyTree;

for(var key in tree) {

    var featureValue = properties[key];
    if(featureValue !== undefined) {
        var treeEntry = tree[key];
        //check the value specific entry
        if(treeEntry.values) {
            var payload = treeEntry.values[featureValue];
            if(payload !== undefined) {
                return payload;
            }
        }

        //check the default entry
        if(treeEntry.default !== undefined) {
            return treeEntry.default;
        }
    }
}

//none found
return null;

var options = {};
options.method = "POST";
options.body = getBody(swneBoundingBox);

return apogee.net.jsonRequest(url,options);

