resource.constructorAddition:
this.mapElement = null;
this.captureButton = null;
this.initialized = false;


this.render = function(data) {
    if(this.geojsonLayer) {
        //remove the old layer
        this.geojsonLayer.remove();
        this.geomjsonLayer = null;
    }

    if(data) {
        if(data.geojson) {
            this.geojsonLayer = L.geoJSON(data.geojson,this.createOptions(data.styleFunctions)).addTo(this.map);
        }

        if(data.bounds) {
            this.map.fitBounds(data.bounds);
        }
    }
}

this.onCapture = function() {
    if(this.map) {
        var mapBounds = this.map.getBounds();
        var swneBoundsArray = [
            mapBounds.getSouth(),
            mapBounds.getWest(),
            mapBounds.getNorth(),
            mapBounds.getEast()
        ]

        mode.getMessenger().dataUpdate("swneBoundingBox",swneBoundsArray);
    }

} 

this.createOptions = function(styleFunctions) {

    var options = {};

    //filter geometry with styles
    options.filter = feature => {
        return styleFunctions.mapFilter(feature);
    }

    //style lines and polygons
    options.style = feature => {
        return styleFunctions.getLineAndAreaStyle(feature);
    }

    //style points
    options.pointToLayer = function(feature,latLng) {
        var iconOptions = styleFunctions.getPointStyle(feature);
        if(iconOptions) {
            var options = {"icon":L.icon(iconOptions)};
            return L.marker(latLng, options);
        }
        else {
            return null;
        }
    };

    //add a popup
    options.onEachFeature = function(feature,layer) {
        var outputLines = [];
        if(feature.properties.name) outputLines.push(feature.properties.name);
        if(feature.properties.amenity) outputLines.push(feature.properties.amenity);
        if(feature.properties.cuisine) outputLines.push(feature.properties.cuisine);

        if(outputLines.length > 0) {
            layer.bindPopup(outputLines.join("<br>"));
        }
    }

    return options;   
}

function getLeafletBounds(swneBoundingBox) {
    var bottomRight = L.latLng(swneBoundingBox[0], swneBoundingBox[1]);
    var topLeft = L.latLng(swneBoundingBox[2], swneBoundingBox[3]);
    return L.latLngBounds(bottomRight, topLeft);
}

resource.onLoad:
if(!this.initialized) {
    //some working variables
    this.mapElement = document.getElementById("mapElement");
    this.captureButton = document.getElementById("captureButton");
    this.captureButton.onclick = () => this.onCapture();

    //create the map
    this.map = L.map('mapElement');

    // create the tile layer with correct attribution
    var osmUrl='http://a.tile.openstreetmap.org/{z}/{x}/{y}.png';
    var osmAttrib='Map data Â© <a href="http://openstreetmap.org">OpenStreetMap</a> contributors';
    var osm = new L.TileLayer(osmUrl, {minZoom: 1, maxZoom: 19, attribution: osmAttrib});		
    this.map.addLayer(osm);

    //control to turn on/off base map
    var layers = {
        "Base Layer": osm
    }
    L.control.layers({},layers).addTo(this.map);

    this.initialized = true;

    //draw the map
    if(this.data) {
        this.render(this.data)
    }
}
