let folderPathPart;
if(folderPath === "") {
    folderPathPart = "";
}
else {
    folderPathPart = ":" + folderPath + ":"; 
}

let requestUrl = driveBaseUrl + folderPathPart + "?expand=children";
return apogeeutil.jsonRequest(requestUrl,options);

let parentEntry;
if(driveResponse.parentReference) {
    let parentPath;
    let parentName;
    if(driveResponse.parentReference.path) {
        parentPath = driveResponse.parentReference.path.substring("/drive/root:".length);
        if(parentPath === "") {
            parentPath = ""
            parentName = "/";
        }
        else {
            parentName = driveResponse.parentReference.name;
        }

        parentEntry = {
           name: parentName,
           path: parentPath,
           type: "folder"
        }
    }
}

if(!parentEntry) parentEntry = null;

return parentEntry;

let children = [];

if(driveResponse.folder) {
    driveResponse.children.forEach( childEntry => {
        let outEntry = {};
        outEntry.name = childEntry.name;
        if(childEntry.file) {
            outEntry.url = childEntry["@microsoft.graph.downloadUrl"];
            outEntry.type = childEntry.file.mimeType;
        }
        if(childEntry.folder) {
            outEntry.path = folderPath + "/" + outEntry.name;
            outEntry.type = "folder";
        }
        let label = outEntry.name + " - " + outEntry.type;
        children.push([label,outEntry]);
    });
}

return children;

let onOpen = formValue => {

    let selected = formValue.selected;

    if(selected.type == "folder") {
        admin.getCommandMessenger().dataCommand("folderPath",selected.path);
    }
    else {
        let contentPromise
        if(selected.type == "application/json") {
            contentPromise = apogeeutil.jsonRequest(selected.url);
        }
        else if(selected.type == "text/plain") {
            contentPromise = apogeeutil.textRequest(selected.url);
        }
        else {
            alert("Only files of type json or plain text can be opened");
            return;
        }

        admin.getCommandMessenger().compoundDataCommand([["fileName",selected.name],["fileContent",contentPromise]]);
    }
}

let allEntries;
if(parentInfo) {
    parentEntry = ["Parent Folder",parentInfo];
    allEntries = [parentEntry].concat(childEntries);
}
else {
    allEntries = childEntries;
}

return [ 
    {
        type: "heading",
        text: "File Path: " + folderPath,
        key: "filePath"
    },
    {
        type: "dropdown",
        label: "Children: ",
        entries: allEntries,
        key: "selected",
    },
    {
        type: "submit",
        onSubmit: onOpen,
        submitLabel: "Open Selected"
    }
]

if(oneDriveCallbackData.hash) {
    let paramArray = oneDriveCallbackData.hash.substring(1).split("&").map(entry => entry.split("="));
    let data = {};
    paramArray.forEach( pair => data[pair[0]] = decodeURIComponent(pair[1]))
    return data;
}
else {
    return null;
}

if((authData)&&(authData.expires_in)) {
    let expiresInSeconds = parseInt(authData.expires_in);
    let nowMs = Date.now();
    let expireMs = nowMs + expiresInSeconds*1000;
    return {
        expireInt: expireMs,
        expireString: (new Date(expireMs)).toLocaleString(),
        loadString: (new Date(nowMs)).toLocaleString()
    }
}

