    delete normalEntryData.name;
    return JSON.stringify(normalEntryData);
}

function getRepeatResult(list) {
    var result = {};

    var getOptionName = record => record.Fields.option_name;
    var standardIndex = list.findIndex(record => (getOptionName(record) =="Standard") );

    if(standardIndex >= 0) {
        bestIndex = standardIndex;
    }
    else {
        var getBestDalue = (bestData,record,index) => {
            var makeBestData = false;
            if(!bestData) {
               makeBestData = true; 
            }
            else {
                var option = getOptionName(record);
                if(option.localeCompare(bestData.option) < 0) {
                    makeBestData = true;
                }
            }

            if(makeBestData) {
                var newBestData = {};
                newBestData.option = getOptionName(record);
                newBestData.index = index;
                return newBestData;
            }
            else {
                return bestData;
            }
        }
        var bestData = list.reduce(getBestDalue,null);
        bestIndex = bestData.index;
    }

    var result = {};
    result.savedRecord = list[bestIndex];
    result.deletedRecords = list.filter( (record,index) => (index != bestIndex) );

    return result;
}

