resource.setData:
if(!data) return;
if(!this.selectedCell) return;
if(!this.availableCell) return;

if( (data.selected === undefined) ||
    (data.available === undefined) ||
    (data.metaFileId === undefined) ||
    (data.existing === undefined)) {
    return;
}

if(this.activeMetaFileId != data.metaFileId) {
    //refresh the selection and exit
    //this will trigger another update
    this.updateSavedSelectedEntries(data.existing,data.available);
    this.activeMetaFileId = data.metaFileId;
    return;
}

this.selectedEntries = data.selected;
this.availableEntries = data.available;
this.existingEntries = data.existing;

this.loadSelectedEntries();
this.loadAvailableEntries();

