this.onSelectClicked = function() {
    var newSelectedEntries = this.addCheckedAndGetNewSelected(this.availableElements);
    this.setNewSelectedEntries(newSelectedEntries);
}

this.onDeselectClicked = function() {
    var newSelectedEntries = this.removeCheckedAndGetNewSelected(this.selectedElements);
    this.setNewSelectedEntries(newSelectedEntries);
}

this.onCommitClicked = function() {

    var taskResult = {};
    taskResult.state = "SUCCESS";
    taskResult.data = {};
    taskResult.data.selectedFileEntries = this.selectedEntries;

    //copy the data to the target recipe entries
    var messenger = mode.getMessenger();
