}

//=====================================
// Initialize Data Methods
//=====================================

this.updateSavedSelectedEntries = function(initialRecipeEntries,availableFileEntries) {
    var newSelectedEntries = this.createSelectedArrayFromExisting(initialRecipeEntries,availableFileEntries);
    var messenger = mode.getMessenger();
    messenger.dataUpdate("selectedFileEntries",newSelectedEntries);
}

this.createSelectedArrayFromExisting = function(existingEntries,availableEntries) {
    var newSelectedEntries = {};

    for(var recipeIdString in existingEntries) {
        var recipeEntry = existingEntries[recipeIdString];
        var fileEntryId = recipeEntry.Fields.file_entry_id;
        if(fileEntryId !== undefined) {
            var availableEntry = availableEntries[fileEntryId];
        }
        else {
            throw new Error("Existing entry ont found in available entries: " + recipeIdString);
        }
        newSelectedEntries[fileEntryId] = availableEntry;
    }

    return newSelectedEntries;
}

//=====================================
// Load Methods
//=====================================

this.loadSelectedEntries = function() {
    apogeeapp.ui.removeAllChildren(this.selectedCell);
    this.selectedElements = [];

    for(var id in this.selectedEntries) {
        var selectedEntry = this.selectedEntries[id];
        this.addListElement(id,selectedEntry,this.selectedCell,this.selectedElements);
    }
}

this.loadAvailableEntries = function() {
    apogeeapp.ui.removeAllChildren(this.availableCell);
    this.availableElements = [];

    for(var id in this.availableEntries) {
        //only includes entries not selected
        var selectedEntry = this.selectedEntries[id];
        if(!selectedEntry) {
            var availableEntry = this.availableEntries[id];
            this.addListElement(id,availableEntry,this.availableCell,this.availableElements);
        }
    }
}

this.addListElement = function(id,entry,parentCell,elementsList) {

    var name = entry.fileTypeEntry.Fields.name + " - " + entry.fileEntry.Fields.option_name;

    var div = document.createElement("div");
    parentCell.appendChild(div);

    var checkbox = document.createElement("input");
    checkbox.setAttribute("type", "checkbox");
    div.appendChild(checkbox);

    var text = document.createTextNode(name);
    div.appendChild(text);

    var element = {};
    element.id = id;
    element.div = div;
    element.checkbox = checkbox;
    elementsList.push(element);

}

//=================================
// Update methods
//=================================

this.addCheckedAndGetNewSelected = function(elementList) {
    var newSelected = apogee.util.deepJsonCopy(this.selectedEntries);
    for(var i = 0; i < elementList.length; i++) {
        var element = elementList[i];
        if(element.checkbox.checked) {
            var id = element.id;
            newSelected[id] = this.availableEntries[id];
        }
    }
    return newSelected;
}

this.removeCheckedAndGetNewSelected = function(elementList) {
    var newSelected = apogee.util.deepJsonCopy(this.selectedEntries);
    for(var i = 0; i < elementList.length; i++) {
        var element = elementList[i];
        if(element.checkbox.checked) {
            var id = element.id;
            delete newSelected[id];
        }
    }
    return newSelected;    
}

this.setNewSelectedEntries = function(newSelectedEntries) {
    var messenger = mode.getMessenger();
    messenger.dataUpdate("selectedFileEntries",newSelectedEntries);
}

