var checkDataInvalid = formData => {
    var selection = formData.entryType;
    if(!selection) {
        return {errorMessage:"A selection must be made"};
    }
    else {
        return false;
    }
}

var returnValue = {};
returnValue.layout = getLayout();
returnValue.doClear = true;
returnValue.clearValue = null;
returnValue.checkDataInvalid = checkDataInvalid;
return returnValue;

