const DEFAULT_EDIT_SELECTION = "typeEntry";

function getValueVariable() {
    return data;
}
function getValueName() {
    return "data";
}

function getLayout() {

    var layout = [];

    //title
    entry = {};
    entry.type = "heading";
    entry.level = 2;
    entry.text = "Select Edit Task";
    layout.push(entry);

    //radio group
    entry = {};
    entry.type = "radioButtonGroup";
    entry.groupName = "rg2";
    entry.entries = [
        ["Create New Type","typeEntry"],
        ["Create New Address Key","addressKey"],
        ["Add Child Property Key","childPropKey"],
        ["Add Child Property Value","childPropValue"],
        ["Create Geometry Theme Rules Entry","geomThemeRulesEntry"],
        ["Create Entity Theme Rules Entry","entityThemeRulesEntry"],
        ["Create Address Label Theme Rules Entry","addressThemeRulesEntry"],
        ["Create Theme Entry","themeEntry"],
        ["Create Input Color","inputColorEntry"],
        ["Create Strings Entry","stringsEntry"]
    ];
    entry.key = "entryType";
    entry.default = DEFAULT_EDIT_SELECTION;
    entry.onChange = onChange;
    layout.push(entry);

    return layout;
}

/////////////////////////////////////////////////////////


function setFormValue(layout,valueVariable) {
    layout.map(element => setElementValue(element,valueVariable));
}

function setElementValue(element,formValue) {
    if(element.type == "panel") {
        //compound element
        if(formValue) {
           var childValue = formValue[element.key];
           setFormValue(element.layout,childValue,null);
        }
    }
    if(element.default != undefined) {
        //simple valued elements
        if((formValue)&&(formValue[element.key] != undefined)) {
            element.value = formValue[element.key];
        }
        else {
            element.value = element.default;
        }

    }
}
