updateData.functionBody:
//---------------
// Create layout
//---------------
const DEFAULT_EDIT_SELECTION = "typeEntry";

var layout = [];

//title
entry = {};
entry.type = "heading";
entry.level = 2;
entry.text = "Select Edit Task";
layout.push(entry);

//radio group
entry = {};
entry.type = "radioButtonGroup";
entry.groupName = "rg2";
entry.entries = [
    ["Create New Type","typeEntry"],
    ["Create New Address Key","addressKey"],
    ["Add Child Property Key","childPropKey"],
    ["Add Child Property Value","childPropValue"],
    ["Create Geometry Theme Rules Entry","geomThemeRulesEntry"],
    ["Create Entity Theme Rules Entry","entityThemeRulesEntry"],
    ["Create Address Label Theme Rules Entry","addressThemeRulesEntry"],
    ["Create Theme Entry","themeEntry"],
    ["Create Input Color","inputColorEntry"],
    ["Create Strings Entry","stringsEntry"]
];
entry.key = "entryType";
entry.default = DEFAULT_EDIT_SELECTION;
layout.push(entry);

entry = {};
entry.type = "textField";
entry.label = "Dummy: "; 
entry.key = "dummy";
layout.push(entry);

//--------------------
// data validation function
// For good data, return false or null
// For invalid data, return {"errorMessage":"<error message here>"}
//--------------------
var checkDataInvalid = formData => {
    var selection = formData.entryType;
    if(!selection) {
        return {errorMessage:"A selection must be made"};
    }
    else {
        return false;
    }
}

//--------------------
// return value
//--------------------
var returnValue = {};
returnValue.layout = layout;
// returnValue.doClear = true;
// returnValue.clearValue = null;
// returnValue.checkDataInvalid = checkDataInvalid;
return returnValue;

//If data valid, return true. If data is invalid, return an error message.
if((!formValue)||(!formValue.entryType)) {
    return "The entry type must be set!";
}
else {
    return true;
}
