updateData.functionBody:
var output = {};
var sessionKey;
if(sessionResponse) {
    if(sessionResponse.responseCode === 1) {
        data = {};
        data.sessionKey = sessionResponse.sessionKey;
        output.data = data;
        taskAppModule.taskCompleted(apogeeMessenger,["login"],["switcher"],data);
        return "SUCCESS";
    }
    else {
        alert("There was an error: " + JSON.stringify(sessionResponse));
        return "ERROR";
    }
}
else {
    return "INCOMPLETE";
}

var data = {};
data.instructionList = instructionList;
data.previousTask = state.previousTask;

return data;

if((!liveInstructionList)||(liveInstructionList.length == 0)) {
    return null; 
}

return micemetarDataAccess.getCommitPromise(liveInstructionList,commitMessage,sessionKey);

var resultValue;

var taskResult = {};
if((!liveInstructionList)||(!commitResponse)) {
    return "WAITING";
}
else if(commitResponse.success === true) {
    data = {};
    data.commitResponse = commitResponse;
    taskAppModule.taskCompleted(apogeeMessenger,["submit"],["completed"],data);
    resultValue = "SUCCESS";
}
else {
    alert("Error making commit: " + commitResponse.msg);
    resultValue = "ERROR";
}

//for error or success - clear the commit request
var updateInfo = [];
updateInfo.push(["liveInstructionList",null]);
updateInfo.push(["commitMessage",null]);
apogeeMessenger.compoundDataUpdate(updateInfo);

return resultValue;

if(!state.active) return null;

var previousTask;
switch(state.previousTask[0]) {
    case "editMeta":
        if(editMeta.editMetaFile.result.valid) {
            return editMeta.editPostProcess.instructionList;   
        }
        else {
            return null;
        }
        break;

    case "editCom":
        if(editCom.editComFile.result.valid) {
            return editCom.editPostProcess.instructionList;   
        }
        else {
            return null;
        }
        break;

    case "editEntry":
        previousTask = editEntry.createInstructionList;
        break;

    default:
        console.log("Unknown task: " + JSON.stringify(state.previousTask));
        return;
}

if( (!activeMetaFileEntry) ||
    (!existingRecipeEntries) ||
    (!availableFileEntries) ||
    (!fileSelection)) {
    return null;
}

var data = {};
data.initialRecipeEntries = existingRecipeEntries;
data.fileEntries = availableFileEntries;
data.activeMetaFileEntry = activeMetaFileEntry;
data.metaFileCreated = !fileSelection.editExisting;
data.metaFileType = metaFileType;

data.insertionInfoOptions = insertionInfoOptions;

return data;

if(!sessionKey) return null;
if(!activeMetaFileEntry)  return null;

var searchDef = {};
searchDef.search = "file_type_entries";
searchDef.id = activeMetaFileEntry.Fields.file_type_id;

return micemetarDataAccess.getRequestPromise(searchDef,sessionKey);

if((!fileTypeEntryList)||(!fileTypeEntryList.success)) return null;

var isFileEntry = (record) => (record.Type == "file_entry");
return fileTypeEntryList.data.Records.filter(isFileEntry);

if(!sessionKey) return null;
if(!context) return null;


var searchDef = {};
searchDef.search = "meta_file_list";
searchDef.file_context = context;

return micemetarDataAccess.getRequestPromise(searchDef,sessionKey);

var output = {};

if((commonData.fileTypeList)&&(commonData.fileTypeList.success)) {
    var fileTypes = {};
    output.fileTypes = fileTypes;

    var loadFileTypeEntry = (record) => { fileTypes[record.Fields.file_type_name] = record.Id };

    var fileTypeRecords = commonData.fileTypeList.data.Response;
    fileTypeRecords.forEach(loadFileTypeEntry);
}

if((metaFileList)&&(metaFileList.success)) {
    var metaFiles = {};
    output.metaFiles = metaFiles;

    var loadMetaFileEntry = (record) => {
        var fileTypeName = getFileTypeName(record.Fields.file_type_id);
        var metaName = record.Fields.name;
        metaFiles[fileTypeName + ":" + metaName] = record.Id;
    }

    var metaFileRecords = metaFileList.data.Response;
    metaFileRecords.forEach(loadMetaFileEntry);
}

output.currentMetaFileId = metaFileId;
output.currentFileTypeId = fileTypeId;
output.context = context;
output.editExisting = fileSelection ? fileSelection.editExisting : false;

output.defaultMetaFileName = commonData.DEFAULT_META_FILE_NAME;

return output;

data = {};
data.customerList = commonData.customerInfo;
data.dummyDropdownEntry = commonData.DUMMY_DROPDOWN_ENTRY;
data.cidContextPrefix = commonData.CID_CONTEXT_PREFIX;
data.globalContext = commonData.GLOBAL_CONTEXT;


return data;

if(editMeta.editMetaFile.result.valid) {
    return editMeta.editMetaFile.result.data;
}
else {
    return null;
}

if(!editResults) return null;

var output= [];

if(editResults.metaFileCreated) {
    output.push(editResults.metaFileEntry);
}

return output;

if(!editResults) return null;

var combinedEntries = editResults.combinedEntries;
var metaFileId = editResults.metaFileEntry.Id; 

var newRecipeNeeded = (combinedEntry) => ((combinedEntry.recipeEntry === undefined)&&(combinedEntry.selected));
var makeRecipeEntry = (combinedEntry) => (createRecipeEntry(combinedEntry,metaFileId));

return combinedEntries.filter(newRecipeNeeded).map(makeRecipeEntry);

updateData.supplementalCode:
function createRecipeEntry(combinedEntry,metaFileId) {

    var fileEntry = combinedEntry.fileEntry;
    var insertionInfo = combinedEntry.insertionInfo;

    var order = 0;

    var fields = {};
    fields.ordr = order;
    fields.meta_file_id = metaFileId;
    fields.file_entry_id = fileEntry.Id;
    fields.insertion_info = insertionInfo;

    return micemetarDataAccess.createRecord("recipe_entry",fields);
}

updateData.functionBody:
if(!editResults) return null;

var combinedEntries = editResults.combinedEntries;

var editRecipeNeeded = (combinedEntry) => ( 
        (combinedEntry.recipeEntry !== undefined) &&
        (combinedEntry.selected) &&
        (insertionInfoChanged(combinedEntry.insertionInfo,combinedEntry.recipeEntry.Fields.insertion_info))
    );

return combinedEntries.filter(editRecipeNeeded).map(getEditRecipePair);

updateData.supplementalCode:
function getEditRecipePair(combinedEntry) {
    var oldRecipeEntry = combinedEntry.recipeEntry;
    var newRecipeEntry = apogee.util.jsonCopy(oldRecipeEntry);
    newRecipeEntry.Fields.insertion_info = combinedEntry.insertionInfo;
    return [oldRecipeEntry,newRecipeEntry];
}

function insertionInfoChanged(info1,info2) {
    if(!info1) {
        if(info2) return true;
        else return false;
    }
    else if(!info2) {
        return true;
    }
    else {
        if(info1.orderedEntry === undefined) throw new Error("Expected an 'orderedEntry' field in insertion info!");

        return (info1.orderedEntry != info2.orderedEntry);
    }
}

updateData.functionBody:
if(!editResults) return null;

var combinedEntries = editResults.combinedEntries;

var deleteRecipeNeeded = (combinedEntry) => ((combinedEntry.recipeEntry !== undefined)&&(!combinedEntry.selected));
var getRecipeEntry = (combinedEntry) => (combinedEntry.recipeEntry);

return combinedEntries.filter(deleteRecipeNeeded).map(getRecipeEntry);

if(!editResults) return null;

var output = [];

//meta file
output = output.concat(createMetaFileEntries.map(micemetarDataAccess.createCreateInstruction));

//parent entries
output = output.concat(createParentFileEntries.map(micemetarDataAccess.createCreateInstruction));

var getEditInstruction = (editPair) => (micemetarDataAccess.createUpdateInstruction(editPair[0],editPair[1]));

output = output.concat(editParentFileEntryPairs.map(getEditInstruction));

output = output.concat(deleteParentFileEntries.map(micemetarDataAccess.createDeleteInstruction));

//recipe entries
output = output.concat(createRecipeEntries.map(micemetarDataAccess.createCreateInstruction));

var getEditInstruction = (editPair) => (micemetarDataAccess.createUpdateInstruction(editPair[0],editPair[1]));

output = output.concat(editRecipeEntryPairs.map(getEditInstruction));

output = output.concat(deleteRecipeEntries.map(micemetarDataAccess.createDeleteInstruction));

return output;

if(!mergedSelections) return null;

var output = [];
for(var idString in mergedSelections) {
    var entry = mergedSelections[idString];
    if((entry.record === undefined)&&(entry.selectedRelation !== undefined)) {
        var metaFileId = parseInt(idString);
        var parentFileEntry = createParentFileEntry(activeMetaFileEntry.Id,metaFileId,entry.selectedRelation);
        output.push(parentFileEntry);
    }
}

return output;

updateData.supplementalCode:
function createParentFileEntry(metaFileId,parentFileId,relation) {
    var order = 0;

    var fields = {};
    fields.meta_file_id = metaFileId;
    fields.parent_file_id = parentFileId;
    fields.relation = relation;

    return micemetarDataAccess.createRecord("meta_parent_entry",fields);
}

updateData.functionBody:
if(!mergedSelections) return null;

var output = [];
for(var idString in mergedSelections) {
    var entry = mergedSelections[idString];
    if((entry.record !== undefined)&&(entry.selectedRelation === undefined)) {
        output.push(entry.record);
    }
}

return output;

if(!mergedSelections) return null;

var output = [];
for(var idString in mergedSelections) {
    var entry = mergedSelections[idString];
    if((entry.record !== undefined)&&(entry.selectedRelation !== undefined)) {
        //this should never be needed, but weill ad it any way

        if(entry.record.Fields.relation != entry.selectedRelation) {
            var newRecord = apogee.util.jsonCopy(entry.record);
            newRecord.Fields.relation = entry.selectedRelation;
            output.push([entry.record,newRecord]);   
        }
    }
}

return output;

if(editMeta.selectDependencies.result.valid) {
    return editMeta.selectDependencies.mergedSelections;
}
else {
    return null;
}

if(!sessionKey) return null;
if(!dependentContexts)  return null;

//request promise from context
var getRequestPromiseFromContext = (context) => {
    var searchDef = {};
    searchDef.search = "meta_file_list";
    searchDef.file_context = context;
    return micemetarDataAccess.getRequestPromise(searchDef,sessionKey);
}

//create promise list
promiseList = dependentContexts.map(getRequestPromiseFromContext);

//evaluate promise list
return Promise.all(promiseList);

if(!metaFileList) return null;

var output = [];

var addToOutput = (response) => {
    if(!response.success) throw new Error("Error loading meta files: " + response.msg);
    output = output.concat(response.data.Response)};

metaFileList.forEach(addToOutput);

return output;

var data = {};

data.layout = layout;
data.onSubmit = (formData) => {
    var refData = formData.parentIds;
    if(!refData) return;

    var needsParent = (depInfo.parentFileType !== null);
    if((needsParent)&&(refData.length === 0)) {
        alert("You must select at least one parent file.");
        return;
    }

    taskAppModule.taskCompleted(apogeeMessenger,["editMeta","selectDependencies"],["editMeta","editMetaFile"],refData);
}
data.onCancel = () => {
    taskAppModule.taskCanceled(apogeeMessenger,["editMeta","selectDependencies"],["editMeta","selectMetaFile"]);
}

return data;

if(!activeMetaFileEntry) return;

var activeFileType = metaFileType;
var activeFileTypeId = activeMetaFileEntry.Fields.file_type_id;
var useType = activeMetaFileEntry.Fields.use_type;
var parentFileType;
var parentFileTypeId;

if(useType == "base") {
    parentFileType = configInfo.baseParentType[activeFileType];
    parentFileTypeId = getFileTypeId(parentFileType);
}
else if(useType == "override") {
    parentFileType = activeFileType;
    parentFileTypeId = activeFileTypeId;
}
else {
    throw new Error("Unrecognized use type: " + useType);
}

var output = {};
output.parentFileType = parentFileType;
output.parentFileTypeId = parentFileTypeId;

return output;

var output = {};
output.lines = [];

if((activeMetaFileEntry)&&(parentMetaFileOptions)) {
    output.lines.push(getTitleLine());
    output.lines.push(getBodyLine());
    output.lines.push(getSubmitLine());
}

return output;

updateData.supplementalCode:
function getTitleLine() {
    var parentType = activeMetaFileEntry.Fields.use_type;
    var title;
    if(parentType == "base") {
        title = "Select Base Parent Files";
    }
    else if(parentType == "override") {
        title = "Select Overridden Parent Files";
    }
    else {
        alert("Unknown parent type: " + parentType);
        title = "ERROR - Unknown parent type: " + parentType;
    }

    return {
		"type": "title",
		"title": title
	};
}

function getSubmitLine() {
    return {
		"type": "submit",
		"submit": "OK",
		"cancel": "Cancel"
	};
}

function getBodyLine() {
    var entries = [];
    var initial = [];
    var addEntry = record => {
        entries.push([getRecordName(record),record.Id]);
        if(isExistingParent(record.Id)) {
            initial.push(record.Id);
        }
    };
    parentMetaFileOptions.forEach(addEntry);

    var line = {};
    line.type = "checkboxGroup";
    line.resultKey = "parentIds";
    line.initial = initial;
    line.entries = entries;
    return line;
}

function getRecordName(record) {
    return getFileTypeName(record.Fields.file_type_id) +
        " - " + record.Fields.name + 
        " - " + record.Fields.file_context;
}

function isExistingParent(recordId) {
    return (existingParentEntries.find(record=>(record.Fields.parent_file_id == recordId)) != null);
}

updateData.functionBody:
if((!result)||(!result.valid)) return null;

var output = {};
var relation = relationForUseType[activeMetaFileEntry.Fields.use_type];

var addExistingRecord = record => {
    var metaFileId = record.Fields.parent_file_id;
    var entry = output[metaFileId];
    if(!entry) {
        entry = {};
        output[metaFileId] = entry;
    }
    entry.record = record;
}

existingParentEntries.forEach(addExistingRecord);

var addSelectedMetaFileId = metaFileId => {
    var entry = output[metaFileId];
    if(!entry) {
        entry = {};
        output[metaFileId] = entry;
    }

    //lokup the type
    entry.selectedRelation = relation;
}

result.data.forEach(addSelectedMetaFileId);

return output;

if(selectMetaContext.result.valid) {
    return selectMetaContext.result.data.context;
}
else {
    return null;
}

if(selectMetaFile.result.valid) {
    return selectMetaFile.result.data;
}
else {
    return null;
}

if(!sessionKey) return null;
if(!fileSelection) return null;


if(fileSelection.editExisting) {
    var searchDef = {};
    searchDef.search = "meta_file_short";
    searchDef.id = fileSelection.metaFileId;
    return micemetarDataAccess.getRequestPromise(searchDef,sessionKey);
}
else {
    return null;
}

if(!fileSelection) return null;

if(fileSelection.editExisting) {
    if(!metaFileResponse) {
        return null;
    }

    if(!metaFileResponse.success) {
        throw new Error("Meta File request error");
    }

    if(metaFileResponse.data.Response.length === 0) {
        throw new Error("Meta file not found");
    }

    return metaFileResponse.data.Response[0];
}
else {
    var fields = {};
    fields.name = fileSelection.metaFileName;
    fields.file_type_id = fileSelection.fileTypeId;
    fields.file_context = context;
    fields.use_type = fileSelection.useType;

    return micemetarDataAccess.createRecord("meta_file",fields);
}

if(activeMetaFileEntry) {
    return getFileTypeName(activeMetaFileEntry.Fields.file_type_id);
}
else {
    return null;
}

if(selectMetaContext.result.valid) {
    return selectMetaContext.result.data.dependentContexts;
}
else {
    return null;
}

if(!metaFileResponse) {
    //empty list
    return [];
}

if(!metaFileResponse.success) {
    throw new Error("Error loading meta file records: " + loadMetaFileRecords.metaFileResponse.msg);
}

var isRecipeEntry = (record) => (record.Type == "recipe_entry");

return metaFileResponse.data.Records.filter(isRecipeEntry);

if(!metaFileResponse) {
    //empty list
    return [];
}

