return metaFileResponse.data.Records.filter(isParentEntry);

var data = {};

if(!state.active) {
    data.layout = commonData.INACTIVE_LAYOUT;
}
else {
    data.layout = layout;
    data.onSubmit = formData => {

        var cidString = formData.cid.trim();
        if(cidString.length === 0) {
            alert("Please enter a CID.");
            return;
        }
        var cid;
        try {
            cid = parseInt(cidString);
        }
        catch(error) {
            alert("Invalid CID: " + error.message);
            return;
        }

        if(!cid) {
            alert("Invalid CID - not a number");
            return;
        }

        var data = {}
        data.cid = cid;

        if(formData.customerContext != commonData.DUMMY_DROPDOWN_ENTRY) {
            data.otherContextStrings = [formData.customerContext];
        }

        taskAppModule.taskCompleted(apogeeMessenger,["editCom","selectCid"],["editCom","editComFile"],data);
    } 
    data.onCancel = () => {
        taskAppModule.taskCanceled(apogeeMessenger,["switcher"],state.previousTask);
    }
}

return data;

//get customer list in proper format
