    output.lines.push(getTitleLine("Select Community Files"));
    addBodyLines(output.lines);
    output.lines.push(getSubmitLine());
}

return output;

updateData.supplementalCode:
function getTitleLine(label) {
    return {
		"type": "title",
		"title": label
	};
}

function getSubmitLine() {
    return {
		"type": "submit",
		"submit": "OK",
		"cancel": "Cancel"
	};
}

function addBodyLines(lines) {
    for(var fileType in sections) {
        var sectionInfo = sections[fileType];
        var fileTypeId = getFileTypeId(fileType);

        var isUniqueBase = sectionInfo.baseIsUnique;
        var isUniqueOverides = false;

        lines.push(getTitleLine(sectionInfo.label));
        lines.push(getSelectionGroupLine(fileType,fileTypeId,true,isUniqueBase));
        lines.push(getSelectionGroupLine(fileType,fileTypeId,false,isUniqueOverrides));
    }
}


function getSelectionGroupLine(fileType,fileTypeId,isBaseFile,isUnique) {
    var isProperType = record => {
        return ( (record.Fields.file_type_id == fileTypeId) &&
            (record.Fields.use_type == (isBaseFile ? "base" : "override")) );
    }

    var entries = [];
    var initial = [];
    var addEntry = record => {
        entries.push([getRecordName(record),record.Id]);
        if(isExistingEntry(record.Id)) {
            initial.push(record.Id);
        }
    };

    availableMetaFiles.filter(isProperType).forEach(addEntry);

    var resultKey = fileType + (isBaseFile ? "_base" : "_override");

    var line = {};
    line.heading = (isBaseFile ? "Base Files" : "Override Files");
    if(isUnique) {
        line.type = "radioButton";
        line.groupName = "selectCom_" + resultKey
    }
    else {
        line.type = "checkboxGroup";
    }
    line.resultKey = resultKey;
    line.initial = initial;
    line.entries = entries;

    return line;
}

function getRecordName(record) {
    return record.Fields.name + 
        " : " + record.Fields.file_context;
}

function isExistingEntry(recordId) {
    return (existingComFileEntries.find(record=>(record.Fields.meta_file_id == recordId)) != null);
}

updateData.functionBody:
var output = {};

output.layout = layout;
output.onSubmit = formData => {

    //make sure data is ok
    if(!verifyData(formData)) {
        return;
    }

    taskAppModule.taskCompleted(apogeeMessenger,["editCom","editComFile"],["submit"],formData);
}

output.onCancel = () => {
    taskAppModule.taskCanceled(apogeeMessenger,["editCom","editComFile"],["editCom","selectCid"]);
}


return output;

if((!result)||(!result.valid)) return null;

var output = {};

var addExistingRecord = record => {
    var metaFileId = record.Fields.meta_file_id;
    var entry = output[metaFileId];
    if(!entry) {
        entry = {};
        output[metaFileId] = entry;
    }
    entry.record = record;
}

existingComFileEntries.forEach(addExistingRecord);

var addSelectedMetaFileId = metaFileId => {
    var entry = output[metaFileId];
    if(!entry) {
        entry = {};
        output[metaFileId] = entry;
    }

    //lokup the type
    entry.selectedForGroup = getFileGroup(metaFileId);
}

for(var entryKey in result.data) {
    var entryResult = result.data[entryKey];
    if(apogee.util.getObjectType(entryResult) == "Array") {
        entryResult.forEach(addSelectedMetaFileId);
    }
    else if(entryResult) {
        addSelectedMetaFileId(entryResult);
    }
}

return output;

updateData.supplementalCode:
function getFileGroup(metaFileId) {
    var record = availableMetaFiles.find( record => (metaFileId == record.Id) );
    if(record) {
        var useType = record.Fields.use_type;
        var ref = refForUseType[useType];
        if(!ref) {
            throw new Error("Reference not found for use type: " + useType);
        }
        else {
            return ref;
        }
    }
    else {
        throw new Error("Meta file not found: id = " + metaFileId);
    }
}

