}

if(initialRecord.Type != targetRecord.Type) {
    throw new Error("Inital and target record types do not match. Target record: " + getRecordErrString(targetRecord));
}

var recordsDifferent = false;

var recordInfo = getRecordInfo(initialRecord.Type);

var instruction = {};
instruction.Type = "Update";
instruction.Record = recordInfo.name;
instruction.Id = initialRecord.Id;

instruction.initialReadPerm = initialRecord.read_perm;
instruction.initialEditPerm = initialRecord.edit_perm;

if(initialRecord.readPerm != targetRecord.readPerm) {
    instruction.targetReadPerm = targetRecord.read_perm;
    recordsDifferent = true;
}

if(initialRecord.editPerm != targetRecord.editPerm) {
    instruction.targetEditPerm = targetRecord.edit_perm;
    recordsDifferent = true;
}

var initialData = {};
instruction.Initial = initialData;
var targetData = {};
instruction.Target = targetData;

for(var i = 0; i < recordInfo.fields.length; i++) {
    var field = recordInfo.fields[i];

    var initialValue;
    var targetValue;

    var initialValue = initialRecord.Fields[field.name];
    var targetValue = targetRecord.Fields[field.name];

    //validate target values
    if(targetValue === undefined) {
        //use default
        targetValue = field.defaultValue;
    }

    if(targetValue === undefined) {
        throw new Error("Unknown problem: undefined field value: " + getFieldErrString(targetRecord,field));
    }

    if(targetValue === null) {
        if(!field.nullOk) {
            throw new Error("Illegal null value: " + getFieldErrString(targetRecord,field));
        }
    }

    if(!fieldsMatch(initialValue,targetValue,field)) {
        initialData[field.name] = initialValue;
        targetData[field.name] = targetValue;
        recordsDifferent = true;
    }
}

if(recordsDifferent) {
    return instruction;
}
else {
    return null;
}

updateData.supplementalCode:
function fieldsMatch(initialValue,targetValue,fieldInfo) {
    if(fieldInfo.fieldClass === "java.lang.String") {
        //compare strings with "=="
        return (initialValue == targetValue);
    }
    else if(fieldInfo.fieldClass.startsWith("java.lang")) {
        //compare other basic values with "==="
        return initialValue === targetValue;
    }
    else if(fieldInfo.fieldClass.startsWith("com.micello")) {
        return initialValue === targetValue;
    }
    else if(fieldInfo.fieldClass == "org.json.JSONObject") {
        return compareJsonData(initialValue,targetValue);
    }
    else if(fieldInfo.fieldClass == "org.json.JSONArray") {
        return compareJsonData(initialValue,targetValue);
    }
    else {
        throw new Error("unrecognized field type: " + fieldInfo.fieldClass);
    }
}

function compareJsonData(a,b) {
    var an = apogee.util.getNormalizedObjectCopy(a);
    var bn = apogee.util.getNormalizedObjectCopy(b);
    var aString = JSON.stringify(an);
    var bString = JSON.stringify(bn);
    return aString == bString;
}

