instruction.targetEditPerm = createRecord.edit_perm;

var targetData = {};
instruction.Target = targetData;

for(var i = 0; i < recordInfo.fields.length; i++) {
    var field = recordInfo.fields[i];
    var targetValue;
    var targetValue = createRecord.Fields[field.name];
    if(targetValue === undefined) {
        //use default
        targetValue = field.defaultValue;
    }

    if(targetValue === undefined) {
        throw new Error("Unknown problem: undefined field value: " + getFieldErrString(createRecord,field));
    }

    if(targetValue === null) {
        if(!field.nullOk) {
            throw new Error("Illegal null value: " + getFieldErrString(createRecord,field));
        }
    }

    targetData[field.name] = targetValue;
}

return instruction;

