resource.constructorAddition:
const CELL_ID_PREFIX = "meta_entry_";
const PREFIX_LENGTH = CELL_ID_PREFIX.length;

const EXPAND_IMAGE_URL = "http://eng.micello.com/apogee/resources/expand.png";
const CONTRACT_IMAGE_URL = "http://eng.micello.com/apogee/resources/contract.png";

this.initialized = false;

this.combinedEntries = null

this.fileEntries = null;
this.initialRecipeEntries = null;
this.activeMetaFileEntry = null;
this.metaFileCreated = false;

this.selectedCell = null;
this.availableCell = null;

this.onCommitClicked = function() {
    var data = {};
    data.combinedEntries = apogee.util.jsonCopy(this.combinedEntries);
    data.metaFileEntry = this.activeMetaFileEntry;
    data.metaFileCreated = this.metaFileCreated;

    taskAppModule.taskCompleted(mode.getMessenger(),["editMeta","editMetaFile"],["submit"],data);
}

this.onResetClicked = function() {
    this.createCombinedEntries(this.initialRecipeEntries,this.fileEntries);
}

this.onCancelClicked = function() {
    taskAppModule.taskCanceled(mode.getMessenger(),["editMeta","editMetaFile"],["editMeta","selectDependencies"]);
}

//=====================================
// Initialize Data Methods
//=====================================

this.createCombinedEntries = function(initialRecipeEntries,fileEntries) {

    var newCombinedEntries = [];

    var tempMap = {};

    var addFileEntryToMap = (record) => {
        var combinedEntry = {}
        combinedEntry.fileEntry = record;
        combinedEntry.selected = false;
        tempMap[record.Id] = combinedEntry;
        newCombinedEntries.push(combinedEntry);
    }
    fileEntries.forEach(addFileEntryToMap);

    var addRecipeEntryToMap = (record) => {
        var combinedEntry = tempMap[record.Fields.file_entry_id];
        if(combinedEntry === undefined) {
            throw new Error("file entry not found: fileEntryId=" + record.Fields.file_entry_id);
        }
        combinedEntry.recipeEntry = record;
        combinedEntry.insertionInfo = record.Fields.insertion_info;
        combinedEntry.selected = true;
    }
    initialRecipeEntries.forEach(addRecipeEntryToMap);

    this.combinedEntries = newCombinedEntries;
}

//=====================================
// Load Methods
//=====================================

this.loadEntries = function() {

    //populate the filter options
    this.populateFilterOptions();

    //populate the display cells
    this.clearSelectedCells();
    apogeeapp.ui.removeAllChildren(this.availableCell);

    var addCombinedEntry = (combinedEntry) => {
        var cellElement = this.createCell(combinedEntry);
        if(combinedEntry.selected) {
            selectedCell = this.getSelectedCell(combinedEntry.insertionInfo);
            selectedCell.appendChild(cellElement);
        }
        else {
            this.availableCell.appendChild(cellElement);
        }
    }

    var filterText = this.freeTextFilterEntry.value;
    var filterRegEx = new RegExp(filterText, "i");
    var freeTextFilter = combinedEntry => {
        if(filterText.length > 0) {
            return (combinedEntry.fileEntry.Fields.entry_name.search(filterRegEx) >= 0);
        }
        else {
            return true;
        }
    }

    this.combinedEntries.filter(this.overallDropdownFilter).filter(freeTextFilter).sort(fileEntrySorter).forEach(addCombinedEntry);
}

function fileEntrySorter(combinedEntry1,combinedEntry2) {
    return combinedEntry1.fileEntry.Fields.entry_name.localeCompare(combinedEntry2.fileEntry.Fields.entry_name);
}

this.createCell = function(combinedEntry) {

    var name = combinedEntry.fileEntry.Fields.entry_name;

    //=============================
    //construction of list element
    //=============================
    var div = document.createElement("div");
    div.id = CELL_ID_PREFIX + combinedEntry.fileEntry.Id;
    div.className = "editMeta_listEntry";

    //draggable interface
    div.draggable = true;
    var instance = this;
    div.ondragstart = (event) => this.onDragStart(event);

    //content
    var minMaxButton = document.createElement("img");
    minMaxButton.src = EXPAND_IMAGE_URL;
    div.appendChild(minMaxButton);

    div.appendChild(document.createTextNode(" "));
    var textSpan = document.createElement("span");
    textSpan.className = "editMeta_entryLabel";
    textSpan.appendChild(document.createTextNode(name));
    div.appendChild(textSpan);

    var entryContentDiv = document.createElement("div");
    entryContentDiv.className = "editMeta_entryDataClosed";
    div.appendChild(entryContentDiv);

    entryContentDiv.appendChild(document.createTextNode("Entry Data: "));
    entryContentDiv.appendChild(document.createElement("br"));

    var preformatDisplay = document.createElement("pre");
    preformatDisplay.className = "editMeta_preformatText";
    var temp = JSON.stringify(combinedEntry.fileEntry.Fields.entry_data,null,"  ");
    temp = temp.replace(/</g,"&lt;");
    temp = temp.replace(/>/g,"&gt;");
    preformatDisplay.innerHTML = temp;
    entryContentDiv.appendChild(preformatDisplay);

    //content interaction
    minMaxButton.onclick = () => {
        if(minMaxButton.src == EXPAND_IMAGE_URL) {
            minMaxButton.src = CONTRACT_IMAGE_URL;
            entryContentDiv.className = "editMeta_entryDataOpened";
        }
        else {
            minMaxButton.src = EXPAND_IMAGE_URL;
            entryContentDiv.className = "editMeta_entryDataClosed";
        }
    }

    return div;
}

//=================================
// selected cell management
//=================================

this.loadSelectedCells = function() {
    apogeeapp.ui.removeAllChildren(this.parentSelectedCell);
    this.selectedCells = {};
    var options = this.getInsertionInfoOptions();

    var numberOptions = options.length;

    if(numberOptions > 1) {
        var selectedTable = document.createElement("table");
        selectedTable.className = "editMeta_table";
        this.parentSelectedCell.appendChild(selectedTable);

        var insertUiEntry = (optionInfo,index) => {

            var row = selectedTable.insertRow(index);
            var cell = row.insertCell(0);
            cell.className = "editMeta_option_container";

            //option title
            var titleDiv = document.createElement("div");
            titleDiv.className = "editMeta_optionTitle";

            var hideButton = document.createElement("img");
            hideButton.src = CONTRACT_IMAGE_URL
            titleDiv.appendChild(hideButton);

            var showButton = document.createElement("img");
            showButton.src = EXPAND_IMAGE_URL;
            showButton.style.display = "none";
            titleDiv.appendChild(showButton);

            var textElement = document.createElement("b");
            textElement.innerHTML = optionInfo.title;
            titleDiv.appendChild(textElement);

            cell.appendChild(titleDiv);

            //option body
            var bodyDiv = document.createElement("div");
            bodyDiv.className = "editMeta_optionDisplayDiv";
            cell.appendChild(bodyDiv);

            var insertionInfo = optionInfo.insertionInfo;

            //events
            bodyDiv.ondragover = (event) => { this.allowDrop(event); }
            bodyDiv.ondrop = (event) => { this.onSelectedDrop(event,insertionInfo); }

            hideButton.onclick = () => this.hideSelectionRow(bodyDiv,hideButton,showButton);
            showButton.onclick = () => this.showSelectionRow(bodyDiv,hideButton,showButton);

            var key = this.getSelectionKey(insertionInfo);
            this.selectedCells[key] = bodyDiv;
        }

        options.forEach(insertUiEntry);

        //clear event handlers from parent
        this.parentSelectedCell.ondragover = undefined;
        this.parentSelectedCell.ondrop = undefined;
    }
    else {
        var selectedCell = this.parentSelectedCell;
        var insertionInfo = options[0].insertionInfo;

        //events
        selectedCell.ondragover = (event) => { this.allowDrop(event); }
        selectedCell.ondrop = (event) => { this.onSelectedDrop(event,insertionInfo); }

        var key = this.getSelectionKey(insertionInfo);
        this.selectedCells[key] = selectedCell;
    }
}

this.getSelectedCell = function(insertionInfo) {
    var key = this.getSelectionKey(insertionInfo);
    var containerCell = this.selectedCells[key];

    if(!containerCell) {
        throw new Error("Insertion info not found: " + JSON.stringify(insertionInfo));
    }

    return containerCell;
}

this.getSelectionKey = function(insertionInfo) {
    return insertionInfo != null ? insertionInfo.orderedEntry : "default";
}

this.clearSelectedCells = function() {
    for(var key in this.selectedCells) {
        apogeeapp.ui.removeAllChildren(this.selectedCells[key]);
    }
}

this.getInsertionInfoOptions = function() {
    var options = this.insertionInfoOptions[this.metaFileType];

    if(!options) {
        options = this.insertionInfoOptions["default"];
    }

    if(!options) {
        throw new Error("Insertion info options not found!");
    }

    return options;
}

this.hideSelectionRow = function(bodyDiv,hideButton,showButton) {
    bodyDiv.style.display = "none";
    hideButton.style.display = "none";
    showButton.style.display = "";
}
this.showSelectionRow = function(bodyDiv,hideButton,showButton) {
    bodyDiv.style.display = "";
    hideButton.style.display = "";
    showButton.style.display = "none";
}
//=================================
// Drag and drop edit methods
//=================================

this.onDragStart = function(event) {
    event.dataTransfer.setData("text",event.target.id);
}

this.allowDrop = function(event) {
    event.preventDefault();
}

this.onSelectedDrop = function(event, insertionInfo) {
    var data = event.dataTransfer.getData("text");
    var combinedEntry = this.lookupCombinedEntry(data);

//    if(!combinedEntry.selected) {
        combinedEntry.selected = true;
        combinedEntry.insertionInfo = insertionInfo;
        this.loadEntries();
//    }
}

this.onAvailableDrop = function(event) {
    var data = event.dataTransfer.getData("text");
    var combinedEntry = this.lookupCombinedEntry(data);

    if(combinedEntry.selected) {
        combinedEntry.selected = false;
        this.loadEntries();
    }
}

this.lookupCombinedEntry = function(divId) {
    var fileEntryId = divId.substr(PREFIX_LENGTH);

    var combinedEntry = this.combinedEntries.find( (entry) => (entry.fileEntry.Id == fileEntryId) );
    if(!combinedEntry) {
        alert("Error: dragged object not found in data");
        return null;
    }

    return combinedEntry;
}

