/* This initializes the filters */
this.initializeFilters = function() {

    //define the filters
    var baseFilterList = [
        ["Section",(fileEntry) => fileEntry.Fields.entry_section],
    ];

    var typeSpecificFilters = this.getTypeSpecificFilters(this.metaFileType);

    var filterList = typeSpecificFilters ? baseFilterList.concat(typeSpecificFilters) : baseFilterList;

    //create the working filter struct
    var createFilterStruct = (filterListEntry) => {
        var struct = {};
        struct.filterFunction = filterListEntry[1];
        struct.title = filterListEntry[0];
        struct.optionSet = null;
        struct.selectionActive = false;
        struct.selection = null;
        struct.selectElement = null;
        return struct;
    }
    this.filterStructs = filterList.map(createFilterStruct); 

    //create the overrall filter function
    this.overallDropdownFilter = (combinedEntry) => {
        var resultOk = true;
        var fileEntry = combinedEntry.fileEntry;

        var singleFilterPass = (filterStruct) => {
            if(filterStruct.selectionActive) {
                 return (filterStruct.filterFunction(fileEntry) == filterStruct.selection);
            }
            else {
                return true;
            }
        }

        return this.filterStructs.every(singleFilterPass);
    }

    //create the fiter display elements
    apogeeapp.ui.removeAllChildren(this.filterCell);
    var createFilterEntry = (filterStruct) => {
        this.filterCell.appendChild(this.getFilterElement(filterStruct));
    }
    this.filterStructs.forEach(createFilterEntry);

    //add the free text filter
    this.filterCell.appendChild(this.createFreeTextFilterElement());
}

/** This creates a dom element for the filter select */
this.getFilterElement = function(filterStruct) {
    var container = document.createElement("div");
    container.className = "editMeta_filterContainerClass";

    container.appendChild(document.createTextNode(filterStruct.title));

    var selectElement = document.createElement("select");
    filterStruct.selectElement = selectElement;
    container.appendChild(selectElement);

    //handler = make selection and reload file entries
    filterStruct.selectElement.onchange = (event) => {
        if(selectElement.value == NULL_SELECTION) {
            filterStruct.selectionActive = false;
            filterStruct.selection = null;
        }
        else {
            filterStruct.selectionActive = true;
            filterStruct.selection = selectElement.value;
        }
        this.loadEntries();
    };

    return container;
}

var NULL_SELECTION = "----";

/** This creates a dom element for the filter select */
this.createFreeTextFilterElement = function() {
    var container = document.createElement("div");
    container.className = "editMeta_filterContainerClass";

    container.appendChild(document.createTextNode("Free text"));

    this.freeTextFilterEntry = document.createElement("input");
    this.freeTextFilterEntry.type = "text";
    this.freeTextFilterEntry.oninput = () => this.onFilterKeyTyped();
    container.appendChild(this.freeTextFilterEntry);

    return container;
}


/** This method populates the options into the filter struct. */
this.populateFilterSelect = function(filterStruct) {
    apogeeapp.ui.removeAllChildren(filterStruct.selectElement);

    filterStruct.selectElement.add(apogeeapp.ui.createElement("option",{"text":NULL_SELECTION}));

    for(var option in filterStruct.optionSet) {
        filterStruct.selectElement.add(apogeeapp.ui.createElement("option",{"text":option}));
    }

    if(filterStruct.selectionActive) {
        filterStruct.selectElement.value = filterStruct.selection;
    }
    else {
        filterStruct.selectElement.value = NULL_SELECTION;
    }
}

/** This method populates the allowed options for each filter in the list. It does this
 * sequentially, meaning the options for later filters depend on the current selection in previous filters. */
this.populateFilterOptions = function() {

    var fileEntryList = this.combinedEntries.map((combinedEntry) => combinedEntry.fileEntry);

    //the create options function, for use with reduce
    var createOptionsAndFilterList = (filterStruct) => {


        //populate filter option set
        var addToOptionSet = (optionSet,fileEntry) => {
            var value = filterStruct.filterFunction(fileEntry);
            optionSet[value] = true;
            return optionSet;
        }

        filterStruct.optionSet = fileEntryList.reduce(addToOptionSet,{});

        //update selection active = clear it if it was set to something that was not an option
        if(filterStruct.selectionActive) {
            if(!filterStruct.optionSet[filterStruct.selection]) {
                filterStruct.selectionActive = false;
                filterStruct.selection = null;
            }
        }

        //filter out list for next filter, if selection active
        fileEntryList = fileEntryList.filter( (fileEntry) => {
            if(filterStruct.selectionActive) {
                 return (filterStruct.filterFunction(fileEntry) == filterStruct.selection);
            }
            else {
                return true;
            }
        });

        //populate the ui element for these new options
        this.populateFilterSelect(filterStruct);
    }

    this.filterStructs.forEach(createOptionsAndFilterList)
}

this.getTypeSpecificFilters = function(metaFileType) {
    return TYPE_SPECIFIC_FILTERS[metaFileType];
}

TYPE_SPECIFIC_FILTERS = {
    "propertyRules":[
        ],
    "themeRules":[
        ["Option",(fileEntry) => fileEntry.Fields.entry_data.propType]
        ],
    "theme":[
        ],
    "strings":[
        ],
    "inputColors":[
        ]
}

const TEXT_FILTER_DELAY = 250;

//text filter methods
this.onFilterKeyTyped = function() {
    if(this.filterTimer) {
        clearTimeout(this.filterTimer);
        this.filterTimer = null;
    }

    this.filterTimer = setTimeout(() => this.updateTextFilter(),TEXT_FILTER_DELAY);
}

this.updateTextFilter = function() {
    //clear timer
    this.filterTimer = null;

    //reload the entries
    this.loadEntries();
}

