resource.setData:
if(!data) return;

if(!this.initialized) {
    this.cachedData = data;
    return;
}

this.editExisting = data.editExisting;

var INVALID_ID = 0;

if(this.selectMetaFileElement) {

    apogeeapp.ui.removeAllChildren(this.selectMetaFileElement);

    //add a dummy entry
    var option = document.createElement("option");
    option.text = "<none selected>";

    option.value = INVALID_ID;
    if(INVALID_ID == data.currentMetaFileId) option.selected = true;
    this.selectMetaFileElement.add(option);

    for(var fileDesc in data.metaFiles) {
        var id = data.metaFiles[fileDesc];
        var option = document.createElement("option");
        option.text = fileDesc;
        option.value = id;
        if(id == data.currentMetaFileId) option.selected = true;
        this.selectMetaFileElement.add(option);
    }

    //set the initial file type, based on a typical setting
    if(data.context == "public") {
        this.baseRadio.checked = true;
    }
    else {
        this.overrideRadio.checked = true;
    }
}

if(this.selectFileTypeElement) {
    apogeeapp.ui.removeAllChildren(this.selectFileTypeElement);

    //add a dummy entry
    var option = document.createElement("option");
    option.text = "<none selected>";
    option.value = INVALID_ID;
    if(INVALID_ID == data.currentFileTypeId) option.selected = true;

    this.selectFileTypeElement.add(option);

    for(var fileDesc in data.fileTypes) {
        var id = data.fileTypes[fileDesc];
        var option = document.createElement("option");
        option.text = fileDesc;
        option.value = id;
        if(id == data.currentFileTypeId) option.selected = true;
        this.selectFileTypeElement.add(option);
    }
}

this.updateUiForCreateUpdate(data.editExisting);

//set a default name, if that field is empty
if(this.metaFileNameInput.value.length === 0) {
    this.metaFileNameInput.value = data.defaultMetaFileName;
}

resource.constructorAddition:
this.data = null;
this.cachedData = null;

this.onSubmitClick = function() {

    var selectElement = this.editExisting ? this.selectMetaFileElement : this.selectFileTypeElement;

    if(!selectElement) {
        alert("Error; UI not initialized");
        return;
    }

    if(selectElement.length === 0) {
        alert("Invalid selection!");
        return;
    }

    var selectedIdString = selectElement.value;
    if(!isFinite(selectedIdString)) {
        alert("Invalid selection!");
        return;
    }
    var selectedId = Number(selectedIdString);
    if(selectedId === 0) {
        if(this.editExisting) {
            alert("You must select a file");
        }
        else {
            alert("You must select a file type");
        }
        return;
    }

    var metaFileName;
    var useType;
    if(!this.editExisting) {
        metaFileName = this.metaFileNameInput.value.trim();
        if((!metaFileName)||(metaFileName.length === 0)) {
            alert("Invalid meta file name!");
            return;
        }

        if(this.baseRadio.checked) {
            useType = "base";
        }
        else if(this.overrideRadio.checked) {
            useType = "override";
        }
        else {
            alert("You must select the file type!");
            return;
        }
    }

    var fileSelection = {};
    fileSelection.editExisting = this.editExisting;
    if(this.editExisting) {
        fileSelection.metaFileId = selectedId;
    }
    else {
        fileSelection.fileTypeId = selectedId;
        fileSelection.metaFileName = metaFileName;
        fileSelection.useType = useType;
    }

    taskAppModule.taskCompleted(mode.getMessenger(),["editMeta","selectMetaFile"],["editMeta","selectDependencies"],fileSelection);
}

this.onCancelClick = function() {
    taskAppModule.taskCanceled(mode.getMessenger(),["editMeta","selectMetaFile"],["editMeta","selectMetaContext"]);
}

this.onRadioClick = function() {
    var editExisting = this.updateRadio.checked;
    this.updateUiForCreateUpdate(editExisting);
}

this.updateUiForCreateUpdate = function(editExisting) {
    this.editExisting = editExisting;

    if(this.selectFileTypeElement) {
        this.selectFileTypeElement.style.display = editExisting ? "none" : "";
    }
    if(this.selectMetaFileElement) {
        this.selectMetaFileElement.style.display = editExisting ? "" : "none";
    }
    if(this.metaFilePanel) {
        this.metaFilePanel.style.display = editExisting ? "none" : "";
    }

    var createRadioSelected = !editExisting;
    var updateRadioSelected = editExisting;

    if((this.createRadio)&&(this.createRadio.checked != createRadioSelected)) {
        this.createRadio.checked = createRadioSelected;
    }
    if((this.updateRadio)&&(this.updateRadio.checked != updateRadioSelected)) {
        this.updateRadio.checked = updateRadioSelected;
    }
}

