resource.constructorAddition:
this.textInput = null;
this.submitButton = null;
this.newLogingButton = null;
this.initialized = false;

this.onSubmit = function() {
    if(!this.initialized) return;

    var context;
    var dependentContexts;
    if(this.cidRadio.checked) {
        var cidString = this.cidInput.value.trim();
        if(cidString.length === 0) {
            alert("Please enter a CID.");
            return;
        }
        var cid;
        try {
            cid = parseInt(cidString);
        }
        catch(error) {
            alert("Invalid CID: " + error.message);
            return;
        }

        if(!cid) {
            alert("Invalid CID - not a number");
            return;
        }

        context = this.cidContextPrefix + cid;

        dependentContexts = [this.globalContext];
        var customerContext = this.customerDropdown.value;
        if(customerContext != this.dummyDropdownEntry) {
            dependentContexts.push(customerContext);
        }

    }
    else if(this.customerRadio.checked) {
        context = this.customerDropdown.value;

        if(context == this.dummyDropdownEntry) {
            alert("Please select a valid customer!");
            return;
        }

        dependentContexts = [this.globalContext];
    }
    else if(this.globalRadio.checked) {
        context = this.globalContext;

        dependentContexts = [this.globalContext];
    }
    else {
        alert("Please select an input option!");
        return;
    }

    var data = {}
    data.context = context;
    if(dependentContexts) {
        data.dependentContexts = dependentContexts;
    }

    taskAppModule.taskCompleted(mode.getMessenger(),["editMeta","selectMetaContext"],["editMeta","selectMetaFile"],data);
}

this.onCancel = function() {
    taskAppModule.taskCanceled(mode.getMessenger(),["editMeta","selectMetaContext"],["switcher"]);
}

this.loadCustomerList = function() {
    //clear any old entries
    this.customerDropdown.innerHTML = "";

    //add default entry
    var entry = document.createElement("option");
    entry.text = this.dummyDropdownEntry;
    entry.value = this.dummyDropdownEntry;
    this.customerDropdown.appendChild(entry);

    var addEntry = element => {
        var entry = document.createElement("option");
        entry.text = element.customerName;
        entry.value = element.authString;
        this.customerDropdown.appendChild(entry);
    }

    this.customerList.forEach(addEntry);

    this.customerDropdown.value = this.dummyDropdownEntry;
}

