resource.constructorAddition:
//this uses the updated version of test authorization that takes and returns a JSON
this.onClick = function() {

    //get the output table
    var outputTableName = "sessionResponse";
    var fromMember = mode.getMember();

    //on click
    var bodyJson = {};
    bodyJson.userName = resource.userInput.value;
    bodyJson.password = resource.passwordInput.value;

    //cleaer password field
    resource.passwordInput.value = "";

    var options = {};
    options.body = JSON.stringify(bodyJson);
    options.method = "POST";

    var requestPromise = apogee.net.jsonRequest(this.loginUrl,options);
    apogee.action.asynchDataUpdate(outputTableName,fromMember,requestPromise);
}

