function processEntry(output,metaFile,recipeEntry,fileEntry,workingData) {
    var entryData = fileEntry.Fields.entry_data;

    var newProperties = updateProperties(entryData.properties);

    //----------------
    //load the section
    //----------------

    var sectionName = fileEntry.Fields.entry_section;
    if(sectionName == "named_entries") {
        //handle generic entries
        var entryName = entryData.name;
        if(namedKeys[entryName]) {
            //this is a named key
            if(!output.named_keys) output.named_keys = {};
            output.named_keys[entryName] = newProperties;
        }
        else {
            //this is a named value
            if(!output.named_groups) output.named_groups = {};
            output.named_groups[entryName] = newProperties;
        }

    }
    else {
        if(!output.property_trees) output.property_trees = {};

        if(entryData.extends) {
            //modify the base section
            baseEntry = getBaseEntry(output.property_trees[sectionName]);
            updateBaseEntry(baseEntry,entryData);
        }
        else if(output.property_trees[sectionName] === undefined) {
            //normal entry
            output.property_trees[sectionName] = newProperties;
        }
        else {
            //this shouldn't happen
            throw new Error("Wasn't expecting another entry!");
        }
    }

}

function updateProperties(properties) {
    var newProperties = [];

    var propCopy = apogee.util.deepJsonCopy(properties);
    for(var i = 0; i < propCopy.length; i++) {
        var property = propCopy[i];

        if(property.input == "type") {
            //remap this!
            var newKey = typeRemap[property.key];

            var newProp = {};
            newProp.key = newKey;
            newProp.input = "multikey";

            //this is a cludge - I should do this another way.
            if(property.meta) newProp.meta = property.meta;

            newProp.required = true;
            newProp.childKeys = [];
            newProperties.push(newProp);

            //handle legacy data addition if applicable
            if(property.legacyKey !== undefined) {
                var modEntry = {};
                modEntry.name = property.name;
                modEntry.properties = [];
                var propEntry = {};
                propEntry.legacyKey = property.legacyKey;
                propEntry.legacyValue = property.legacyValue;
                propEntry.value = property.value;
                propEntry.key = property.key;
                propEntry.action = "modifies";
                modEntry.properties.push(propEntry);

                updateBaseEntry(newProp,modEntry);
            }
        }
        else {
            //keep as is
            newProperties.push(property);
        }
    }

    return newProperties;
}

function updateBaseEntry(baseEntry,entryData) {
    var extendingProp = entryData.properties[0];
    if(!extendingProp.action) throw new Error("Expected a 'modifies' entry!");

    var key = extendingProp.legacyKey;
    var value = extendingProp.legacyValue;

    var childKeys = baseEntry.childKeys;
    if(!childKeys) {
        childKeys = [];
        baseEntry.childKeys = childKeys;
    }

    var keyEntry = getChildKeyEntry(childKeys,key);

    var valueEntry = {};
    keyEntry.values[value] = valueEntry;

    //add any child keys for the value
    if(entryData.properties.length > 1) {

        var valueChildKeys = [];
        valueEntry.childKeys = valueChildKeys;
        for(var i = 1; i < entryData.properties.length; i++) {
            var entry = entryData.properties[i];
            valueChildKeys.push(entry);
        }
    }
}

function getBaseEntry(section) {
    return section[0];
}

function getChildKeyEntry(childKeys,key) {
    var keyEntry;
    for(var i = 0; i < childKeys.length; i++) {
        var entry = childKeys[i];
        if(entry.key == key) {
            keyEntry = entry;
        }
    }
    if(!keyEntry) {
        keyEntry = {};
        keyEntry.key = key;
        keyEntry.input = "enum";
        keyEntry.values = {};
        childKeys.push(keyEntry);
    }

    return keyEntry;
}

