function convertKeyList(inList) {
    var outList = [];
    for(var i = 0; i < inList.length; i++) {
        var inEntry = inList[i];
        outList.push(getOutEntry(inEntry));
    }
    return outList;
}

function convertValueMap(inMap) {
    var outMap = {};
    for(var tag in inMap) {
        var inEntry = inMap[tag];
        outMap[tag] = getOutEntry(inEntry);
    }
    return outMap;
}

function getOutEntry(inEntry) {
    var outEntry = {};
    for(var inTag in inEntry) {
        var outTag = tagRename[inTag];
        if(outTag === undefined) outTag = inTag;

        var inValue = inEntry[inTag];

        var outValue;
        if(inTag == "values") {
            outValue = convertValueMap(inValue);
        }
        else if(inTag == "childKeys") {
            outValue = convertKeyList(inValue);
        }
        else {
            outValue = inValue;
        }

        outEntry[outTag] = outValue;
    }

    //special changes
    if(outEntry.input == "named_entry") {
        var entryName = outEntry.named_entry;
        if(namedKeys[entryName]) {
            //named key
            outEntry.input = "named_key";
            outEntry.name = entryName;
            delete outEntry.named_entry;
        }
        else {
            //named group
            outEntry.input = "named_group";
            outEntry.name = entryName;
            delete outEntry.named_entry;
        }
    }

    if(outEntry.action == "delete") {
        outEntry.input = "fixed";
        delete outEntry.action;
    }

    return outEntry;
}

function convert(data) {
    var objectType = apogee.util.getObjectType(data);
    if(objectType == "Object") {
        return convertObject(data);
    }
    else if(objectType == "Array") {
        return convertArray(data);
    }
    else {
        return data;
    }
}

function convertObject(data) {
    for(var key in data) {
        var value = data[key];

        if(key == "desc") {
            delete data.desc;
        }
        else if((key == "has_lang")&&(value == false)) {
            delete data.has_lang;
        }
        else {
            data[key] = convert(value);
        }
    }
    return data;
}

function convertArray(data) {
    for(var i = 0; i < data.length; i++) {
        data[i] = convert(data[i]);
    }
    return data;
}

