resource.constructorAddition:
this.textInput = null;
this.submitButton = null;
this.newLogingButton = null;
this.initialized = false;

this.onSubmit = function() {
    if(!this.initialized) return;

    var context = this.textInput.value.trim();
    if(context.length === 0) {
        alert("Invalid context value.");
        return;
    }

    var output = {};
    output.state = "SUCCESS";
    output.data = {}
    output.data.context = context;

    var messenger = mode.getMessenger();
    messenger.dataUpdate("taskResults.loadContext",output);
}

this.onNewLogin = function() {
    mode.getMessenger().dataUpdate("taskResults.login","");
}
