const propTypeToKeyTag = {
    "address":"ak",
    "entity":"ek",
    "geometry":"gk"
};

//read all in entries and add them as out entries to the output priority section
function processInputEntries(inEntryMap,outPriorityEntry) {
    for(var tag in inEntryMap) {
        var inEntry = inEntryMap[tag];
        processInEntry(inEntry,outPriorityEntry)
    }
    return outEntryList;
}

//add this in entry to the output priority section
function processInEntry(inEntry,outPriorityEntry) {
    //only process if there is a payload - there should be
    if(inEntry.payload) {
        var pathType = inEntry.propType;
        var path = inEntry.path;
        var keyIndex = path.length-1;
        var rootEntry = outPriorityEntry;
        //read the proper key or value entry from the path
        var resultEntry = getEntry(pathType,path,keyIndex,rootEntry);
        addPayload(resultEntry,inEntry.payload);
    }
}

//copies data from the payload to the entry
function addPayload(entry,payload) {
    for(var tag in payload) {
        entry[tag] = payload[tag];
    }
}

//This function looks up the entry as the given index of the path
function getEntry(pathType,path,keyIndex,rootEntry) {
    if(path.length === 0) {
        //the root entry is the value entry
        return rootEntry;
    }   
    else {
        var parentValueEntry;

        //get the parent value entry
        if(keyIndex === 0) {
            parentValueEntry = rootEntry;
        }
        else {
            var parentIndex = keyIndex - 1;
            //we require all entries excpet that last is a key-value entry
            if(path[parentIndex].length !== 2) throw new Error("Error: all path entries except the last must be a full key-value entry! path: " + path);

            parentValueEntry = getEntry(pathType,path,parentIndex,rootEntry);
        }

        //get the key or value element at this entry
        var currentPathElement = path[keyIndex];
        var key = currentPathElement[0];
        var keyEntry = getKeyEntryFromValueEntry(parentValueEntry,pathType,key);
        if(currentPathElement.length === 1) {
            //this is a key entry
            return keyEntry;
        }
        else {
            //this is a value entry
            var value = currentPathElement[1];
            return getValueEntryFromKeyEntry(keyEntry,value);
        }
    }
}

function getValueEntryFromKeyEntry(keyEntry,value) {
    //get the value map from the key entry
    var valueMap = keyEntry.v;
    if(valueMap === undefined) {
       valueMap = {};
       keyEntry.v = valueMap;
    }
    //lookup up the value entry
    valueEntry = valueMap[value];

    if(valueEntry === undefined) {
        //construct a new one if it doesn't exist
        valueEntry = {};
        valueMap[value] = valueEntry;
    }
    return valueEntry;
}

function getKeyEntryFromValueEntry(valueEntry,propType,key) {
    //key varies by prop type: ak/ek/gk
    var keyTag = propTypeToKeyTag[propType];

    //get the key list from the value entry
    var keyList = valueEntry.ks;
    if(keyList === undefined) {
        keyList = [];
        valueEntry.ks = keyList;
    }
    //check if the key entry already exists
    for(var i = 0; i < keyList.length; i++) {
        var keyEntry = keyList[i];
        if(keyEntry[keyTag] == key) {
            return keyEntry;
        }
    }
    //make a new key entry
    var keyEntry = {};
    keyEntry[keyTag] = key;
    keyList.push(keyEntry);
    return keyEntry;
}

