function createTreeOutEntry(inEntry,section) {
    if(inEntry.extends !== undefined) {
        return createExtendsEntry(inEntry,section);
    }
    else if(inEntry.modifies !== undefined) {
        return createModifiesEntry(inEntry,section);
    }
    else {
        throw new Error("Preset override supports only entry action extends and modifies");
    }
}

function createExtendsEntry(inEntry,section) {
    if(inEntry.properties.length > 1) {
        throw new Error("Preset override supports only extend entries with no new properties");
    }

    var firstProp = inEntry.properties[0];

    if((firstProp.action != "modifies")&&(firstProp.key != "featureType")) {
        throw new Error("Preset override supports only extend entries for feature types");
    }

    var legacyKey = firstProp.legacyKey;
    var legacyValue = firstProp.legacyValue;


    //this is a simple new key-value
    var outEntry = {};
    outEntry.type = "value";
    outEntry.tree = section;
    outEntry.path = [legacyKey];
    outEntry.value = legacyValue;
    outEntry.entry = {};

    return outEntry;
}

function createModifiesEntry(inEntry,section) {
    //lookup base entry
    var parentName = inEntry.modifies;

    var parentFileEntry =  loadMetaFileRecords.getPropRuleFileEntry(section,parentName);
    if(!parentFileEntry) {
        throw new Error("Parent not found: " + section + ":" + parentName);
    }
    parentEntryData = parentFileEntry.Fields.entry_data;

    //sanity check - because we are implmenting a subset of valid cases here
    var parentFeatureTypeProp = parentEntryData.properties[0];
    if(parentFeatureTypeProp.key != "featureType") {
        throw new Error("Preset override failed on reading base entry for modifies: " + entryValue);
    }

    var parentLegacyKey = parentFeatureTypeProp.legacyKey;
    var parentLegacyValue = parentFeatureTypeProp.legacyValue;

    //read the inEntry
    if(inEntry.properties.length > 1) {
        throw new Error("Preset override reconstruct only supports single property entries! Entry value = " + inEntry.name);
    }
    var childProp = inEntry.properties[0];

    if(childProp.action == "modifies") {
        return createChildValueEntry(section,parentLegacyKey,parentLegacyValue,childProp);
    }
    else if(childProp.action === undefined) {
        return createChildKeyEntry(section,parentLegacyKey,parentLegacyValue,childProp);
    }
    else {
        throw new Error("Unsupported property action in modifies entry: " + childProp.action);
    }
}

function createChildKeyEntry(section,parentLegacyKey,parentLegacyValue,childProp) {

    if(childProp.type != "enum") {
        throw new Error("Preset override reconstruct supports only child key entry of type enum: childProp key = " + childProp.key);
    }

    //for enum, update the values format
    var newValues = {};
    childProp.values.forEach((element) => {newValues[element.value] = {}});

    var newData = {};
    newData.k = childProp.key;
    newData.type = childProp.input;
    newData.values = newValues;

    var outEntry = {};
    outEntry.type = "value";
    outEntry.tree = section;
    outEntry.path = [parentLegacyKey,parentLegacyValue];
    outEntry.value = legacyValue;
    outEntry.entry = newData;

    return outEntry;
}

function createChildValueEntry(section,parentLegacyKey,parentLegacyValue,childProp) {

    var childKey = childProp.key;

    if(childProp.values.length > 1) {
        throw new Error("Preset override reconstruct supports only child value entry with a single added value: childProp key = " + childKey);
    }

    var childValue = childProp.values[0].value;

    var outEntry = {};
    outEntry.type = "value";
    outEntry.tree = section;
    outEntry.path = [parentLegacyKey,parentLegacyValue,childKey];
    outEntry.value = childValue;
    outEntry.entry = {};

    return outEntry;
}

function createNamedOutEntryList(inEntry) {
    //we support a very restricted set in this conversion - address key additions
    if(inEntry.modifies != "address keys") {
        throw new Error("Unsupported named entry in preset overrides conversion: " + inEntry.modifies);
    }

    if(inEntry.properties.length !== 1) {
        throw new Error("Preset override resonctruct supports only a single property!");
    }
    var addressProp = inEntry.properties[0];
    if(addressProp.key != "address keys") {
        throw new Error("Unsupported named entry key in preset overrides conversion: " + addressProp.key);
    }
    if(addressProp.action != "modifies") {
        throw new Error("Unsupported named key action in preset overrides conversion: " + addressProp.action);
    }

    var createNewEntry = (listEntry) => ({"name": "address keys","value": listEntry.value,"entry": {}});
    var outEntryList = addressProp.values.map(createNewEntry);

    return outEntryList;
}

