function convertKeyList(inList) {
    var outList = [];
    for(var i = 0; i < inList.length; i++) {
        var inEntry = inList[i];
        outList.push(getOutEntry(inEntry));
    }
    return outList;
}

function convertValueList(inList) {
    var outMap = {};
    for(var i = 0; i < inList.length; i++) {
        var inEntry = inList[i];
        var tag = inEntry.value;
        outMap[tag] = getOutEntry(inEntry);
    }
    return outMap;
}

function getOutEntry(inEntry) {
    var outEntry = {};
    for(var inTag in inEntry) {
        if(inTag == "value") continue;

        var outTag = tagRename[inTag];
        if(outTag === undefined) outTag = inTag;

        var inValue = inEntry[inTag];

        var outValue;
        if(inTag == "values") {
            outValue = convertValueList(inValue);
        }
        else if(inTag == "properties") {
            outValue = convertKeyList(inValue);
        }
        else if(inTag == "keys") {
            outValue = convertKeyList(inValue);
        }
        else if(inTag == "options") {
            outValue = convertKeyList(inValue);
        }
        else {
            outValue = inValue;
        }

        outEntry[outTag] = outValue;
    }

    //special changes
    if(outEntry.input == "named_entry") {
        var entryName = outEntry.named_entry;
        if(namedKeys[entryName]) {
            //named key
            outEntry.input = "named_key";
            outEntry.name = entryName;
            delete outEntry.named_entry;
        }
        else {
            //named group
            outEntry.input = "named_group";
            outEntry.name = entryName;
            delete outEntry.named_entry;
        }
    }

    if(outEntry.input == "type") {
        outEntry.input = "multikey";
        outEntry.k = typeRemap[outEntry.k];
    }

    if(outEntry.action == "delete") {
        outEntry.input = "fixed";
        delete outEntry.action;
    }
    if(outEntry.action == "modifies") {
        //this won't work in all cases - just the ones we have so far!!!
        delete outEntry.action;
    }

    return outEntry;
}

