function processEntry(outFile,metaFile,recipeEntry) {

    var fileEntry = loadMetaFileRecords.getFileEntry(recipeEntry.Fields.file_entry_id);
    if(!fileEntry) {
        throw new Error("File entry not found. id=" + recipeEntry.Fields.file_entry_id);
    }

    //get the section or subsection to which this should be added
    var outSection;
    var sectionName = fileEntry.Fields.entry_section;
    var insertionInfo = recipeEntry.Fields.insertion_info;
    if(insertionInfo != null) {
        //allow for the different types of insertion info
        var subsectionEntry;
        if(insertionInfo.taggedEntry !== undefined) {
            subsectionEntry = getTaggedSubsection(outFile,sectionName,insertionInfo.taggedEntry);
        }
        else if(insertionInfo.orderedEntry !== undefined) {
            subsectionEntry = getOrderedSubsection(outFile,sectionName,insertionInfo.orderedEntry);
        }
        else {
            throw new Error("unrecognized subsection type");
        }
        outSection = subsectionEntry;
    } 
    else if(sectionName) {
        //standard case - no recipe entry insertion info
        outSection = getMapSection(outFile,sectionName);
    }
    else {
        //no section
        outSection = outFile.data;
    }


    //insert the data
    var key = fileEntry.Fields.entry_value;
    var data = fileEntry.Fields.entry_data;

    if(outSection[key] !== undefined) {
        throw new Error("Repeat key! section: " + sectionName + ". Key: " + key);
    }

    outSection[key] = data;
}

function getMapSection(outFile,sectionName) {
    var sectionEntry = outFile.data[sectionName];
    if(sectionEntry === undefined) {
        sectionEntry = {};
        outFile.data[sectionName] = sectionEntry;
    }
    return sectionEntry;
}

function getTaggedSubsection(outFile,sectionName,tag) {
    var sectionEntry = getMapSection(outFile,sectionName);
    var subsectionEntry = sectionEntry[tag];
    if(subsectionEntry === undefined) {
        subsectionEntry = {};
        sectionEntry[tag] = subsectionEntry;
    }
    return subsectionEntry;
}

function getOrderedSubsection(outFile,sectionName,order) {
    //read the section
    var sectionEntry = outFile.data[sectionName];
    if(!sectionEntry) {
        sectionEntry = [];
        outFile.data[sectionName] = sectionEntry;
    }

    //get or add the subsection, from the proper ordered location
    var subsectionEntry;
    var index = 0;
    if(sectionEntry.length > 0) {
        do {
            var entry = sectionEntry[index];
            if(entry.order === order) {
                subsectionEntry = entry;
            }
            index++;
        } while((entry.order < order)&&(index < sectionEntry.length));
    }

    if(!subsectionEntry) {
        subsectionEntry = {};
        subsectionEntry.order = order;
        subsectionEntry.entries = {};
        sectionEntry.splice(index,0,subsectionEntry);
    }
    return subsectionEntry.entries;
}

