var value;
var result = {};

var cid = FIRST_CID;


var successCid = function(json) {

    //make sure the json has a cid!
    if(json.cid === undefined) {
        json.cid = cid;
    }

    var entry = {};
    entry.success = true;
    entry.data = json;
    result[cid] = entry;

    return getNextCid();
}

var errorCid = function(msg) {
    var entry = {};
    entry.success = false;
    entry.data = msg;
    result[cid] = entry;

    return getNextCid();
}

var getCid = function(cid) {
    console.log("Requesting " + cid);
    url = apogee.util.formatString(urlTemplate,cid,key);
    return apogee.net.jsonRequest(url).then(successCid).catch(errorCid);
}

var getNextCid = function() {

    //get the next if we are not finihed yet
    cid++;
    if(cid <= LAST_CID) {
        return getCid(cid);
    } 
    else {
        return result;
    }
}

return getCid(cid);










