function isNone(overrideJson) {
    if(overrideJson.none) {
        return true;
    }
    else {
        return false;
    }
}

 function isEmpty(overrideJson) {   
    //neglect themes for now
    if((overrideJson.presetOverrides !== undefined)&&(overrideJson.presetOverrides.length > 0)) {
        return false;
    }
    else if((overrideJson.themeMapOverrides !== undefined)&&(overrideJson.themeMapOverrides.length > 0)) {
        return false;
    }
    else if((overrideJson.stringsOverrides !== undefined)&&(overrideJson.stringsOverrides.length > 0)) {
        return false;
    }
    else if(overrideJson.themeOverrides !== undefined) {
        for(var themeName in overridesJson.themeOverrides) {
            var list = overridesJson.themeOverrides[themeName];
             if((list !== undefined)&&(list.length > 0)) {
                return false;
            }
        }
    }
    else {
        return true;
    }
}
