function getInstructionForCombinedEntry(combinedEntry) {
    var newEntry = combinedEntry.newEntry;
    var oldEntry = combinedEntry.oldEntry;

    if(newEntry) {
        if(!oldEntry) {
            console.log("Create: " + newEntry.Id);
            return createCreateInstruction(newEntry);
        }
        else {
            //this will return null for matching records
            console.log("Update(maybe): " + newEntry.Id);
            return createUpdateInstruction(oldEntry,newEntry);
        }
    }
    else if(oldEntry) {
        console.log("Delete: " + oldEntry.Id);
        return createDeleteInstruction(oldEntry);
    }

    return null;
}

function entriesDifferent(entry1,entry2) {
    var string1 = JSON.stringify(apogee.util.getNormalizedCopy(entry1));
    var string2 = JSON.stringify(apogee.util.getNormalizedCopy(entry2));
    return (string1 == string2);
}

