function addSearchResponse(searchResponse,outputMap) {
    if(!searchResponse) {
        return;
    }

    if(!searchResponse.success) {
        throw new Error("Error in loading record data: " + searchResponse.msg);
    }

    var searchResponseData = searchResponse.data;

    if(searchResponseData.Response) {
        addRecordList(searchResponseData.Response,outputMap);
    }
    if(searchResponseData.Records) {
        addRecordList(searchResponseData.Records,outputMap);
    }

}

function addRecordList(recordList,outputMap) {
    for(var i = 0; i < recordList.length; i++) {
        var record = recordList[i];

        var typeSection = outputMap[record.Type];
        if(!typeSection) {
            typeSection = {};
            outputMap[record.Type] = typeSection;
        }

        //if there is already a record, just overwrite it. They should be the same.
        typeSection[record.Id] = record;
    }
}

