function fieldsMatch(initialValue,targetValue,fieldInfo) {
    if(fieldInfo.fieldClass === "java.lang.String") {
        //compare strings with "=="
        return (initialValue == targetValue);
    }
    else if(fieldInfo.fieldClass.startsWith("java.lang")) {
        //compare other basic values with "==="
        return initialValue === targetValue;
    }
    else if(fieldInfo.fieldClass.startsWith("com.micello")) {
        return initialValue === targetValue;
    }
    else if(fieldInfo.fieldClass == "org.json.JSONObject") {
        return compareJsonData(initialValue,targetValue);
    }
    else if(fieldInfo.fieldClass == "org.json.JSONArray") {
        return compareJsonData(initialValue,targetValue);
    }
    else {
        throw new Error("unrecognized field type: " + fieldInfo.fieldClass);
    }
}

function compareJsonData(a,b) {
    var an = apogee.util.getNormalizedObjectCopy(a);
    var bn = apogee.util.getNormalizedObjectCopy(b);
    var aString = JSON.stringify(an);
    var bString = JSON.stringify(bn);
    return aString == bString;
}

