var onSubmit = (formData) => {

    //on click
    var bodyJson = {};
    bodyJson.userName = formData.userName;
    bodyJson.password = formData.password;

    var options = {};
    options.body = JSON.stringify(bodyJson);
    options.method = "POST";

    var requestPromise = apogee.net.jsonRequest(loginUrl,options);

    //set the response
    apogeeMessenger.asynchDataUpdate("sessionResponse",requestPromise);

    //this clears the password...
    apogeeMessenger.dataUpdate("savedUserName",formData.userName);
}

var layout = [];

//title
entry = {};
entry.type = "heading";
entry.level = 2;
entry.text = "Login";
layout.push(entry);

//text entry
entry = {};
entry.type = "textField";
entry.label = "User Name: ";
entry.key = "userName";
entry.value = savedUserName;
layout.push(entry);

//text entry
entry = {};
entry.type = "textField";
entry.label = "Password: ";
entry.password = true;
entry.key = "password";
layout.push(entry);

//suvbmit (no cancel)
entry = {};
entry.type = "submit";
entry.onSubmit = onSubmit;
layout.push(entry);

return layout;

var msg;

if(!sessionResponse) {
    msg = "Login Needed!";
    style = "color:red";
}
else if(sessionResponse.responseCode == 1) {
    msg = "Login Success";
    style = "color:blue";
}
else {
    msg = "Login Failure: " + sessionResponse.message;
    style = "color:red";
}

var textEntry = {};
textEntry.type = "htmlDisplay";
textEntry.html = "<span style='" + style + "' + >" + msg + "</span>";

return [textEntry];

if((!sessionResponse)||(!sessionResponse.paramTable)) {
    throw new Error("Param table not loaded!");
}

//we need to accomadate both http and https patterns
var httpsUrl;
var httpUrl;
if(inUrl.startsWith("https:")) {
    httpsUrl = inUrl;
    httpUrl = "http" + inUrl.substring(5);
}
else if(inUrl.startsWith("http:")) {
    httpsUrl = "https" + inUrl.substring(4);
    httpUrl = inUrl;
}
else throw new Error("Unknown protocol! Use http or https.");

var isMatchingKey = (paramEntry) => {
    if(paramEntry.urlPattern.startsWith("http:")) return httpUrl.startsWith(paramEntry.urlPattern);
    else if(paramEntry.urlPattern.startsWith("https:")) return httpsUrl.startsWith(paramEntry.urlPattern);
    else throw new Error("Unknown reference URL protocol: " + paramEntry.urlPattern);
}
var paramEntry = sessionResponse.paramTable.find(isMatchingKey);

if(paramEntry) {
    if(paramEntry.params.length > 1) {
        throw new Error("Code only supports 1 appended parameter. (Is an easy fix to add more)")
    }
    else if(paramEntry.params.length === 1) {
        return inUrl + "?" + paramEntry.params[0][0] + "=" + paramEntry.params[0][1];
    }
}

//no params to add
return inUrl;
