updateData.supplementalCode:
function checkForControls(folderComponents) {
    for(var tableName in folderComponents) {
        var tableJson = folderComponents[tableName];

        if(tableJson.type == "apogeeapp.app.CustomControlComponent") {
            var resource =  tableJson.resource;
            var customizeScript = resource.customizeScript;
            if(customizeScript !== undefined) {
                delete resource.customizeScript;
                resource.setData = customizeScript;
            }
            var supplementalCode = tableJson.resource.supplementalCode;
            if(supplementalCode !== undefined) {
                delete resource.supplementalCode;
                resource.constructorAddition = supplementalCode;
            }
            alert("Custom control needs fixing: " + tableName);
        }

        if(tableJson.children) {
            checkForControls(tableJson.children);
        }
    }
}
