function getRotatedPoint(unrotatedPoint) {
    var rotatedPoint = [];
    rotatedPoint[0] = unrotatedPoint[0] * Math.cos(rotAngleRad) - unrotatedPoint[1] * Math.sin(rotAngleRad);
    rotatedPoint[1] = unrotatedPoint[0] * Math.sin(rotAngleRad) + unrotatedPoint[1] * Math.cos(rotAngleRad);
    return rotatedPoint;
}

updateData.functionBody:
var deltaTheta = Math.atan(textHeight/textWidth);

var thetaA = rotAngleRad + deltaTheta;
var thetaB = rotAngleRad - deltaTheta;

var lengthA = getRotatedLength(thetaA,labelWidth,labelHeight);
var lengthB = getRotatedLength(thetaB,labelWidth,labelHeight);

var diagLength = (lengthA < lengthB ) ? lengthA : lengthB;

var length = Math.abs(diagLength * Math.cos(deltaTheta));

console.log(lengthA + ", " + lengthB + ", " + diagLength + ", " + length);

var scale = length/textWidth;

return scale;

updateData.supplementalCode:
function getRotatedLength(theta,labelWidth,labelHeight) {
    if(Math.abs(Math.tan(theta)) < (labelHeight / labelWidth) ) {
        return labelWidth / Math.cos(theta);
    }
    else {
        return labelHeight / Math.sin(theta);
    }
}
