updateData.functionBody:
var onSubmit = formData => {
    if((!formData.name)||(formData.name.length === 0)) {
        alert("Invalid name!");
        return;
    }
    apogeeMessenger.dataUpdate("inputValue",formData);
}

var onCancel = () => {
    alert("canceled!");
}

//create the form layout
var layout = [];
var entry;

//title
entry = {};
entry.type = "heading";
entry.level = 2;
entry.text = "Data Entry Form";
layout.push(entry);

//text entry
entry = {};
entry.type = "textField";
entry.label = "Name: ";
entry.key = "name";
entry.value = inputValue.name;
layout.push(entry);

//select group 1
entry = {};
entry.type = "dropdown";
entry.label = "Gender: ";
entry.entries = ["male","female","other"];
entry.value = inputValue.gender;
entry.key = "gender";
layout.push(entry);

entry = {};
entry.type = "submit";
entry.onSubmit = onSubmit;
entry.onCancel = onCancel;
layout.push(entry);

return layout;
