updateData.functionBody:
var defaultValue = {
	"text1": "Hello There",
	"radioGroup1": "dog"
}

//we will use the messeneger to write the data to the myOutput table
var onSubmit = (formValue,formObject) => {
    apogeeMessenger.dataUpdate("myOutput",formValue);    
};

//on cancel we will reset the value of the form to the default
var onCancel = (formObject) => {
    formObject.setValue(defaultValue);    
};

return [
	{
		"type": "heading",
		"level": 2,
		"text": "Test Form"
	},
	{
		"type": "textField",
		"label": "Test text entry: ",
		"value": defaultValue.text1,
		"key": "text1"
	},
	{
		"type": "radioButtonGroup",
		"label": "Radio 1: ",
		"groupName": "rg1",
		"entries": [
			"antelope",
			"buffalo",
			"cat",
			"dog"
		],
		"value": defaultValue.radioGroup1,
		"key": "radioGroup1"
	},
	{
	    "type": "submit",
	    "submitLabel": "Submit",
	    "cancelLabel": "Reset",
	    "onSubmit": onSubmit,
	    "onCancel": onCancel
	}
];
